        "inquirer": "^8.1.5",
        "plist": "^3.0.4",
        "semver": "^7.3.5",
        "xcode": "3.0.1"
      },
      "devDependencies": {
        "@types/assert": "^1.5.2",
        "@types/mkdirp": "^1.0.1",
        "@types/mocha": "^9.0.0",
        "@types/node": "^14.0.27",
        "@types/q": "^1.5.4",
        "archiver": "latest",
        "body-parser": "latest",
        "code-push-plugin-testing-framework": "file:./code-push-plugin-testing-framework",
        "del": "v6.0.0",
        "express": "latest",
        "mkdirp": "latest",
        "mocha": "^9.2.0",
        "q": "^1.5.1",
        "run-sequence": "latest",
        "shx": "^0.3.4",
        "slash": "^3.0.0",
        "tslint": "^6.1.3",
        "typescript": "^4.4.3"
      }
