{
	"repository.callsign" : "G",
	"phabricator.uri" : "https://reviews.freebsd.org/",
	"arc.land.onto.default": "main",
	"arc.land.onto": ["main"]
}

{
  "exclude": "(contrib|crypto)",
  "linters": {
    "python": {
      "type": "pep8",
      "include": "(\\.py$)"
    },
    "spelling": {
      "type": "spelling"
    },
    "chmod": {
      "type": "chmod",
      "exclude": "(/tests/)"
    },
    "merge-conflict": {
      "type": "merge-conflict"
    },
    "filename": {
      "type": "filename"
    },
    "json": {
      "type": "json",
      "include": "(\\.arclint|\\.json$)"
    }
  }
}
