	if (val >= 0)
		SStream_concat(ss, "#%" PRIu32, val);
	else {
		if (val == INT32_MIN)
			SStream_concat(ss, "#-%" PRIu32, val);
		else
			SStream_concat(ss, "#-%" PRIu32, (uint32_t)-val);
	}
}

