    pkt_comments       = true;
    cap_file_more_info = true;
    cap_file_idb       = true;
    cap_file_nrb       = true;
    cap_file_dsb       = true;

    cap_data_size      = true;
    cap_duration       = true;
    cap_earliest_packet_time = true;
    cap_latest_packet_time = true;
    cap_order          = true;

    cap_data_rate_byte = true;
    cap_data_rate_bit  = true;
    cap_packet_size    = true;
