def on_msg_receive(msg):
    if msg.out and not binlog_done:
      return;

    if msg.dest.id == our_id: # direct message
      peer = msg.src
    else: # chatroom
      peer = msg.dest

    pp.pprint(msg)
    if msg.text.startswith("!ping"):
      peer.send_msg("PONG! google.com", preview=False, reply=msg.id)


