#include <string.h>

#define CPU_UNKNOWN     	0
#define CPU_ARMV6       	1
#define CPU_ARMV7       	2
#define CPU_CORTEXA9       	3
#define CPU_CORTEXA15       	4

static char *cpuname[] = {
  "UNKNOWN",
  "ARMV6",
