int get_feature(char *search)
{

#ifdef __linux
	FILE *infile;
  	char buffer[2048], *p,*t;
  	p = (char *) NULL ;

  	infile = fopen("/proc/cpuinfo", "r");

	while (fgets(buffer, sizeof(buffer), infile))
	{

		if (!strncmp("Features", buffer, 8))
		{
			p = strchr(buffer, ':') + 2;
			break;
      		}
  	}

  	fclose(infile);


	if( p == NULL ) return 0;

	t = strtok(p," ");
	while( t = strtok(NULL," "))
	{
		if (!strcmp(t, search))   { return(1); }
	}

#endif
	return(0);
}


int detect(void)
{

#ifdef __linux

	FILE *infile;
  	char buffer[512], *p;
  	p = (char *) NULL ;

  	infile = fopen("/proc/cpuinfo", "r");
	while (fgets(buffer, sizeof(buffer), infile))
	{

		if (!strncmp("CPU part", buffer, 8))
		{
			p = strchr(buffer, ':') + 2;
			break;
      		}
  	}

  	fclose(infile);
  	if(p != NULL) {
	  if (strstr(p, "0xc09")) {
	    return CPU_CORTEXA9;
	  }
	  if (strstr(p, "0xc0f")) {
	    return CPU_CORTEXA15;
	  }
	  if (strstr(p, "0xd07")) {
	    return CPU_ARMV7;  //ARMV8 on 32-bit
	  }

	}

  	p = (char *) NULL ;
  	infile = fopen("/proc/cpuinfo", "r");

	while (fgets(buffer, sizeof(buffer), infile))
	{

		if ((!strncmp("model name", buffer, 10)) || (!strncmp("Processor", buffer, 9)))
		{
			p = strchr(buffer, ':') + 2;
			break;
      		}
  	}

  	fclose(infile);

  	if(p != NULL)
	{

		if (strstr(p, "ARMv7"))
		{
			 if ( get_feature("vfpv4"))
			 	return CPU_ARMV7;

			 if ( get_feature("vfpv3"))
			 	return CPU_ARMV7;

			 if ( get_feature("vfp"))
			 	return CPU_ARMV6;


		}

		if (strstr(p, "ARMv6"))
		{
			 if ( get_feature("vfp"))
			 	return CPU_ARMV6;
		}


	}

  	p = (char *) NULL ;
  	infile = fopen("/proc/cpuinfo", "r");

	while (fgets(buffer, sizeof(buffer), infile))
	{

		if ((!strncmp("CPU architecture", buffer, 16)))
		{
			p = strchr(buffer, ':') + 2;
			break;
      		}
  	}
  	fclose(infile);
  	if(p != NULL) {
	  if (strstr(p, "8")) {
	    return CPU_ARMV7;  //ARMV8 on 32-bit
	  }

	}

#endif

	return CPU_UNKNOWN;
}

