	if (cpulowperf >0)
	printf("#define NUM_CORES_LP %d\n",cpulowperf);
	if (cpumidperf >0)
	printf("#define NUM_CORES_MP %d\n",cpumidperf);
	if (cpuhiperf >0)
	printf("#define NUM_CORES_HP %d\n",cpuhiperf);
#endif	
}



