#define CPU_UNKNOWN     0
#define CPU_P5600       1
#define CPU_1004K	2
#define CPU_24K		3

static char *cpuname[] = {
  "UNKNOWN",
  "P5600",
  "MIPS1004K",
  "MIPS24K"
};

int detect(void){

#ifdef __linux
  FILE *infile;
  char buffer[512], *p;

  p = (char *)NULL;
  infile = fopen("/proc/cpuinfo", "r");
  while (fgets(buffer, sizeof(buffer), infile)){
    if (!strncmp("cpu", buffer, 3)){
	p = strchr(buffer, ':') + 2;
#if 0
	fprintf(stderr, "%s \n", p);
#endif
	break;
      }
  }

  fclose(infile);

  if(p != NULL){
  if (strstr(p, "5600")) {
    return CPU_P5600;
  } else if (strstr(p, "1004K")) {
    return CPU_1004K;
  } else if (strstr(p, " 24K")) {
    return CPU_24K;
  } else  
    return CPU_UNKNOWN;
  }
#endif
    return CPU_UNKNOWN;
}

char *get_corename(void){
  return cpuname[detect()];
}

void get_architecture(void){
  printf("MIPS");
}

void get_subarchitecture(void){
  if(detect()==CPU_P5600|| detect()==CPU_1004K|| detect()==CPU_24K){
    printf("P5600");
  }else{
    printf("UNKNOWN");
  }
}

void get_subdirname(void){
  printf("mips");
}

void get_cpuconfig(void){
  if(detect()==CPU_P5600){
    printf("#define P5600\n");
    printf("#define L1_DATA_SIZE 65536\n");
    printf("#define L1_DATA_LINESIZE 32\n");
    printf("#define L2_SIZE 1048576\n");
    printf("#define L2_LINESIZE 32\n");
    printf("#define DTB_DEFAULT_ENTRIES 64\n");
    printf("#define DTB_SIZE 4096\n");
    printf("#define L2_ASSOCIATIVE 8\n");
  } else if (detect()==CPU_1004K) {
    printf("#define MIPS1004K\n");
    printf("#define L1_DATA_SIZE 32768\n");
    printf("#define L1_DATA_LINESIZE 32\n");
    printf("#define L2_SIZE 262144\n");
    printf("#define DTB_DEFAULT_ENTRIES 8\n");
    printf("#define DTB_SIZE 4096\n");
    printf("#define L2_ASSOCIATIVE 4\n");
  } else if (detect()==CPU_24K) {
    printf("#define MIPS24K\n");
    printf("#define L1_DATA_SIZE 32768\n");
    printf("#define L1_DATA_LINESIZE 32\n");
    printf("#define L2_SIZE 32768\n");
    printf("#define DTB_DEFAULT_ENTRIES 8\n");
    printf("#define DTB_SIZE 4096\n");
    printf("#define L2_ASSOCIATIVE 4\n");
  }else{
    printf("#define UNKNOWN\n");
  }
#ifndef NO_MSA  
  if (get_feature("msa")) printf("#define HAVE_MSA\n");
#endif
}

void get_libname(void){
  if(detect()==CPU_P5600) {
    printf("p5600\n");
  } else if (detect()==CPU_1004K) {
    printf("mips1004K\n");
  } else if (detect()==CPU_24K) {
    printf("mips24K\n");
  }else{
    printf("mips\n");
  }
}

int get_feature(char *search)
{

#ifdef __linux
        FILE *infile;
        char buffer[2048], *p,*t;
        p = (char *) NULL ;

        infile = fopen("/proc/cpuinfo", "r");

        while (fgets(buffer, sizeof(buffer), infile))
        {

                if (!strncmp("Features", buffer, 8) || !strncmp("ASEs implemented", buffer, 16))
                {
                        p = strchr(buffer, ':') + 2;
                        break;
                }
        }

        fclose(infile);

        if( p == NULL ) return 0;

        t = strtok(p," ");
        while( t = strtok(NULL," "))
        {
                if (strstr(t, search))   { return(1); }
        }

#endif
        return(0);
}

