#define CPU_UNKNOWN         0
#define CPU_MIPS64_GENERIC  1
#define CPU_SICORTEX        2
#define CPU_LOONGSON3R3     3
#define CPU_LOONGSON3R4     4
#define CPU_I6400           5
#define CPU_P6600           6
#define CPU_I6500           7

static char *cpuname[] = {
  "UNKNOWN",
  "MIPS64_GENERIC"
  "SICORTEX",
  "LOONGSON3R3",
  "LOONGSON3R4",
  "I6400",
  "P6600",
  "I6500"
};

int detect(void){

#ifdef linux
  FILE *infile;
  char buffer[512], *p;

  p = (char *)NULL;
  //Check model name for Loongson3
  infile = fopen("/proc/cpuinfo", "r");
  while (fgets(buffer, sizeof(buffer), infile)){
    if (!strncmp("model name", buffer, 10)){
      p = strchr(buffer, ':') + 2;
      break;
    }
  }
  fclose(infile);
  if (p != NULL){
    if (strstr(p, "Loongson-3A3000") || strstr(p, "Loongson-3B3000")){
      return CPU_LOONGSON3R3;
    } else if (strstr(p, "Loongson-3A4000") || strstr(p, "Loongson-3B4000")){
      return CPU_LOONGSON3R4;
    } else{
      return CPU_SICORTEX;
    }
  }

  return CPU_MIPS64_GENERIC;
#else
  return CPU_UNKNOWN;
#endif
}

