#define CPU_GENERIC         0
#define CPU_C910V           1
#define CPU_x280            2
#define CPU_RISCV64_ZVL256B 3
#define CPU_RISCV64_ZVL128B 4

static char *cpuname[] = {
  "RISCV64_GENERIC",
  "C910V",
  "x280",
  "CPU_RISCV64_ZVL256B",
  "CPU_RISCV64_ZVL128B"
};

static char *cpuname_lower[] = {
  "riscv64_generic",
  "c910v",
  "x280",
  "riscv64_zvl256b",
  "riscv64_zvl128b"
};

int detect(void){
#ifdef __linux
  FILE *infile;
  char buffer[512],isa_buffer[512],model_buffer[512];
  const char* check_c910_str = "T-HEAD C910";
  char *pmodel = NULL, *pisa = NULL;

  infile = fopen("/proc/cpuinfo", "r");
  if (!infile)
    return CPU_GENERIC;
  while (fgets(buffer, sizeof(buffer), infile)){
    if(!strncmp(buffer, "model name", 10)){
      strcpy(model_buffer, buffer);
      pmodel = strchr(model_buffer, ':');
      if (pmodel)
        pmodel++;
    }

    if(!strncmp(buffer, "isa", 3)){
      strcpy(isa_buffer, buffer);
      pisa = strchr(isa_buffer, '4');
      if (pisa)
        pisa++;
    }
  }

  fclose(infile);

  if (!pmodel || !pisa)
   return(CPU_GENERIC);

  if (strstr(pmodel, check_c910_str) && strchr(pisa, 'v'))
    return CPU_C910V;

  return CPU_GENERIC;
#endif

  return CPU_GENERIC;
}

