  if (get_vendor() == VENDOR_INTEL) {
      if(LD1.size<=0 || LC1.size<=0){
	//If we didn't detect L1 correctly before,
	int count;
	for (count=0;count <4;count++) {
	cpuid_count(4, count, &eax, &ebx, &ecx, &edx);
        switch (eax &0x1f) {
        case 0:
          continue;
          case 1:
          case 3:
          {
            switch ((eax >>5) &0x07)
            {
            case 1:
            {
//            fprintf(stderr,"L1 data cache...\n");
            int sets = ecx+1;
            int lines = (ebx & 0x0fff) +1;
            ebx>>=12;
            int part = (ebx&0x03ff)+1;
            ebx >>=10;
            int assoc = (ebx&0x03ff)+1;
            LD1.size = (assoc*part*lines*sets)/1024;
            LD1.associative = assoc;
            LD1.linesize= lines;
            break;
            }
            default: 
              break;
           }
          break;
          }
         case 2:
          {
            switch ((eax >>5) &0x07)
            {
            case 1:
            {
//            fprintf(stderr,"L1 instruction cache...\n");
            int sets = ecx+1;
            int lines = (ebx & 0x0fff) +1;
            ebx>>=12;
            int part = (ebx&0x03ff)+1;
            ebx >>=10;
            int assoc = (ebx&0x03ff)+1;
            LC1.size = (assoc*part*lines*sets)/1024;
            LC1.associative = assoc;
            LC1.linesize= lines;
            break;
            }
            default: 
              break;
           }
          break;

          }
          default:
          break;
        }
      }
    }
    cpuid(0x80000000, &cpuid_level, &ebx, &ecx, &edx);
    if (cpuid_level >= 0x80000006) {
      if(L2.size<=0){
	//If we didn't detect L2 correctly before,
	cpuid(0x80000006, &eax, &ebx, &ecx, &edx);

	L2.size         = BITMASK(ecx, 16, 0xffff);
	L2.associative  = BITMASK(ecx, 12, 0x0f);

	switch (L2.associative){
	case 0x06:
	  L2.associative = 8;
	  break;
	case 0x08:
	  L2.associative = 16;
	  break;
	}

	L2.linesize     = BITMASK(ecx,  0, 0xff);
      }
    }
  }

  if ((get_vendor() == VENDOR_AMD) ||
      (get_vendor() == VENDOR_HYGON) ||
      (get_vendor() == VENDOR_CENTAUR) ||
      (get_vendor() == VENDOR_ZHAOXIN)) {
    cpuid(0x80000005, &eax, &ebx, &ecx, &edx);

    LDTB.size        = 4096;
    LDTB.associative = BITMASK(eax, 24, 0xff);
    if (LDTB.associative == 0xff) LDTB.associative = 0;
    LDTB.linesize    = BITMASK(eax, 16, 0xff);

    LITB.size        = 4096;
    LITB.associative = BITMASK(eax,  8, 0xff);
    if (LITB.associative == 0xff) LITB.associative = 0;
    LITB.linesize    = BITMASK(eax,  0, 0xff);

    DTB.size        = 4;
    DTB.associative = BITMASK(ebx, 24, 0xff);
    if (DTB.associative == 0xff) DTB.associative = 0;
    DTB.linesize    = BITMASK(ebx, 16, 0xff);

    ITB.size        = 4;
    ITB.associative = BITMASK(ebx,  8, 0xff);
    if (ITB.associative == 0xff) ITB.associative = 0;
    ITB.linesize    = BITMASK(ebx,  0, 0xff);

    LD1.size        = BITMASK(ecx, 24, 0xff);
    LD1.associative = BITMASK(ecx, 16, 0xff);
    if (LD1.associative == 0xff) LD1.associative = 0;
    LD1.linesize    = BITMASK(ecx,  0, 0xff);

    LC1.size        = BITMASK(ecx, 24, 0xff);
    LC1.associative = BITMASK(ecx, 16, 0xff);
    if (LC1.associative == 0xff) LC1.associative = 0;
    LC1.linesize    = BITMASK(ecx,  0, 0xff);

    cpuid(0x80000006, &eax, &ebx, &ecx, &edx);

    L2LDTB.size        = 4096;
    L2LDTB.associative = BITMASK(eax, 24, 0xff);
    if (L2LDTB.associative == 0xff) L2LDTB.associative = 0;
    L2LDTB.linesize    = BITMASK(eax, 16, 0xff);

    L2LITB.size        = 4096;
    L2LITB.associative = BITMASK(eax,  8, 0xff);
    if (L2LITB.associative == 0xff) L2LITB.associative = 0;
    L2LITB.linesize    = BITMASK(eax,  0, 0xff);

    L2DTB.size        = 4;
    L2DTB.associative = BITMASK(ebx, 24, 0xff);
    if (L2DTB.associative == 0xff) L2DTB.associative = 0;
    L2DTB.linesize    = BITMASK(ebx, 16, 0xff);

    L2ITB.size        = 4;
    L2ITB.associative = BITMASK(ebx,  8, 0xff);
    if (L2ITB.associative == 0xff) L2ITB.associative = 0;
    L2ITB.linesize    = BITMASK(ebx,  0, 0xff);

    if(L2.size <= 0){
      //If we didn't detect L2 correctly before,
      L2.size        = BITMASK(ecx, 16, 0xffff);
      L2.associative = BITMASK(ecx, 12, 0xf);
      switch (L2.associative){
      case 0x06:
	L2.associative = 8;
	break;
      case 0x08:
	L2.associative = 16;
	break;
      }

      if (L2.associative == 0xff) L2.associative = 0;
      L2.linesize    = BITMASK(ecx,  0, 0xff);
    }

    L3.size        = BITMASK(edx, 18, 0x3fff) * 512;
    L3.associative = BITMASK(edx, 12, 0xf);
    if (L3.associative == 0xff) L2.associative = 0;
    L3.linesize    = BITMASK(edx,  0, 0xff);

  }

    switch (type) {

    case CACHE_INFO_L1_I :
      *cacheinfo = LC1;
      break;
    case CACHE_INFO_L1_D :
      *cacheinfo = LD1;
      break;
    case CACHE_INFO_L2 :
      *cacheinfo = L2;
      break;
    case CACHE_INFO_L3 :
      *cacheinfo = L3;
      break;
    case CACHE_INFO_L1_DTB :
      *cacheinfo = DTB;
      break;
    case CACHE_INFO_L1_ITB :
      *cacheinfo = ITB;
      break;
    case CACHE_INFO_L1_LDTB :
      *cacheinfo = LDTB;
      break;
    case CACHE_INFO_L1_LITB :
      *cacheinfo = LITB;
      break;
    case CACHE_INFO_L2_DTB :
      *cacheinfo = L2DTB;
      break;
    case CACHE_INFO_L2_ITB :
      *cacheinfo = L2ITB;
      break;
    case CACHE_INFO_L2_LDTB :
      *cacheinfo = L2LDTB;
      break;
    case CACHE_INFO_L2_LITB :
      *cacheinfo = L2LITB;
      break;
    }
  return 0;
}

int get_cpuname(void){

  int family, exfamily, model, vendor, exmodel, stepping;

  if (!have_cpuid()) return CPUTYPE_80386;

  family   = get_cputype(GET_FAMILY);
  exfamily = get_cputype(GET_EXFAMILY);
  model    = get_cputype(GET_MODEL);
  exmodel  = get_cputype(GET_EXMODEL);
  stepping = get_cputype(GET_STEPPING);

  vendor = get_vendor();

  if (vendor == VENDOR_INTEL){
    switch (family) {
    case 0x4:
      return CPUTYPE_80486;
    case 0x5:
      return CPUTYPE_PENTIUM;
    case 0x6:
      switch (exmodel) {
      case 0:
	switch (model) {
	case  1:
	case  3:
	case  5:
	case  6:
#if defined(__x86_64__) || defined(__amd64__)
	  return CPUTYPE_CORE2;
#else
	  return CPUTYPE_PENTIUM2;
#endif
	case  7:
	case  8:
	case 10:
	case 11:
	  return CPUTYPE_PENTIUM3;
	case  9:
	case 13:
	case 14:
	  return CPUTYPE_PENTIUMM;
	case 15:
	  return CPUTYPE_CORE2;
	}
	break;
      case 1:  // family 6 exmodel 1
	switch (model) {
	case  6:
	  return CPUTYPE_CORE2;
	case  7:
	  return CPUTYPE_PENRYN;
	case 10:
	case 11:
	case 14:
	case 15:
	  return CPUTYPE_NEHALEM;
	case 12:
	  return CPUTYPE_ATOM;
	case 13:
	  return CPUTYPE_DUNNINGTON;
	}
	break;
      case  2: // family 6 exmodel 2
	switch (model) {
	case 5:
	  //Intel Core (Clarkdale) / Core (Arrandale)
	  // Pentium (Clarkdale) / Pentium Mobile (Arrandale)
	  // Xeon (Clarkdale), 32nm
	  return CPUTYPE_NEHALEM;
	case 10:
	  //Intel Core i5-2000 /i7-2000 (Sandy Bridge)
	  if(support_avx())
	    return CPUTYPE_SANDYBRIDGE;
	  else
	    return CPUTYPE_NEHALEM; //OS doesn't support AVX
	case 12:
	  //Xeon Processor 5600 (Westmere-EP)
	  return CPUTYPE_NEHALEM;
	case 13:
	  //Intel Core i7-3000 / Xeon E5 (Sandy Bridge)
	  if(support_avx())
	    return CPUTYPE_SANDYBRIDGE;
	  else
	    return CPUTYPE_NEHALEM;
	case 14:
	  // Xeon E7540
	case 15:
	  //Xeon Processor E7 (Westmere-EX)
	  return CPUTYPE_NEHALEM;
	}
	break;
      case 3: // family 6 exmodel 3
	switch (model) {
	case  7:
	    // Bay Trail	
	    return CPUTYPE_ATOM;	
	case 10:
        case 14:
	  // Ivy Bridge
	  if(support_avx())
	    return CPUTYPE_SANDYBRIDGE;
	  else
	    return CPUTYPE_NEHALEM;
        case 12:
	case 15:
          if(support_avx2())
            return CPUTYPE_HASWELL;
          if(support_avx())
	    return CPUTYPE_SANDYBRIDGE;
          else
	    return CPUTYPE_NEHALEM;
	case 13:
	  //Broadwell
          if(support_avx2())
            return CPUTYPE_HASWELL;
          if(support_avx())
	    return CPUTYPE_SANDYBRIDGE;
          else
	    return CPUTYPE_NEHALEM;
	}
	break;
      case 4: // family 6 exmodel 4
        switch (model) {
        case 5:
	case 6:
          if(support_avx2())
            return CPUTYPE_HASWELL;
          if(support_avx())
	    return CPUTYPE_SANDYBRIDGE;
          else
	    return CPUTYPE_NEHALEM;
	case 7:
	case 15:
	  //Broadwell
          if(support_avx2())
            return CPUTYPE_HASWELL;
          if(support_avx())
	    return CPUTYPE_SANDYBRIDGE;
          else
	    return CPUTYPE_NEHALEM;
	case 14:
	  //Skylake
          if(support_avx2())
            return CPUTYPE_HASWELL;
          if(support_avx())
	    return CPUTYPE_SANDYBRIDGE;
          else
	    return CPUTYPE_NEHALEM;
	case 12:
	  // Braswell
	case 13:
	  // Avoton
	    return CPUTYPE_NEHALEM;
        }
        break;
      case 5:  // family 6 exmodel 5
        switch (model) {
	case 6:
	  //Broadwell
          if(support_avx2())
            return CPUTYPE_HASWELL;
          if(support_avx())
	    return CPUTYPE_SANDYBRIDGE;
          else
	    return CPUTYPE_NEHALEM;
	case 5:
	  // Skylake X
          if(support_avx512_bf16())
            return CPUTYPE_COOPERLAKE;
          if(support_avx512())
            return CPUTYPE_SKYLAKEX;
          if(support_avx2())
            return CPUTYPE_HASWELL;
          if(support_avx())
	    return CPUTYPE_SANDYBRIDGE;
	  else
	  return CPUTYPE_NEHALEM;
        case 14:
	  // Skylake
          if(support_avx2())
            return CPUTYPE_HASWELL;
          if(support_avx())
	    return CPUTYPE_SANDYBRIDGE;
          else
	    return CPUTYPE_NEHALEM;
	case 7:
	    // Xeon Phi Knights Landing
          if(support_avx2())
            return CPUTYPE_HASWELL;
          if(support_avx())
	    return CPUTYPE_SANDYBRIDGE;
          else
	    return CPUTYPE_NEHALEM;
	case 12:
	    // Apollo Lake
	case 15:
	    // Denverton		
	    return CPUTYPE_NEHALEM;
	}
	break;
      case 6:  // family 6 exmodel 6
        switch (model) {
        case 6: // Cannon Lake
          if(support_avx512())
            return CPUTYPE_SKYLAKEX;
          if(support_avx2())
            return CPUTYPE_HASWELL;
          if(support_avx())
	    return CPUTYPE_SANDYBRIDGE;
	  else
	  return CPUTYPE_NEHALEM;
	case 10: // Ice Lake SP
	  if(support_avx512_bf16())
            return CPUTYPE_COOPERLAKE;	
          if(support_avx512())
            return CPUTYPE_SKYLAKEX;
          if(support_avx2())
            return CPUTYPE_HASWELL;
          if(support_avx())
	    return CPUTYPE_SANDYBRIDGE;
	  else
	  return CPUTYPE_NEHALEM;	
        }
      break;
      case 7: // family 6 exmodel 7
        switch (model) {
	case 10: // Goldmont Plus
	    return CPUTYPE_NEHALEM;
        case 14: // Ice Lake
          if(support_avx512())
            return CPUTYPE_SKYLAKEX;
          if(support_avx2())
            return CPUTYPE_HASWELL;
          if(support_avx())
	    return CPUTYPE_SANDYBRIDGE;
	  else
	  return CPUTYPE_NEHALEM;
        }
      break;
      case 8:      
        switch (model) {
        case 12: // Tiger Lake
        case 13: // Tiger Lake (11th Gen Intel(R) Core(TM) i7-11800H @ 2.30GHz)
          if(support_avx512())
            return CPUTYPE_SKYLAKEX;
          if(support_avx2())
            return CPUTYPE_HASWELL;
          if(support_avx())
            return CPUTYPE_SANDYBRIDGE;
          else
          return CPUTYPE_NEHALEM;
	case 14: // Kaby Lake and refreshes
          if(support_avx2())
            return CPUTYPE_HASWELL;
          if(support_avx())
	    return CPUTYPE_SANDYBRIDGE;
          else
	    return CPUTYPE_NEHALEM;
	case 15: // Sapphire Rapids
	  if(support_amx_bf16())
	    return CPUTYPE_SAPPHIRERAPIDS;
	  if(support_avx512_bf16())
            return CPUTYPE_COOPERLAKE;	
          if(support_avx512())
            return CPUTYPE_SKYLAKEX;
          if(support_avx2())
            return CPUTYPE_HASWELL;
          if(support_avx())
	    return CPUTYPE_SANDYBRIDGE;
	  else
	  return CPUTYPE_NEHALEM;	
        }
      break;
      case 9:
        switch (model) {
        case 7: // Alder Lake desktop
        case 10: // Alder Lake mobile
	  if(support_avx512_bf16())
            return CPUTYPE_COOPERLAKE;	
          if(support_avx512())
            return CPUTYPE_SKYLAKEX;
          if(support_avx2())
            return CPUTYPE_HASWELL;
          if(support_avx())
            return CPUTYPE_SANDYBRIDGE;
          else
          return CPUTYPE_NEHALEM;
        case 13: // Ice Lake NNPI
          if(support_avx512())
            return CPUTYPE_SKYLAKEX;
          if(support_avx2())
            return CPUTYPE_HASWELL;
          if(support_avx())
            return CPUTYPE_SANDYBRIDGE;
          else
          return CPUTYPE_NEHALEM;
	case 14: // Kaby Lake and refreshes
          if(support_avx2())
            return CPUTYPE_HASWELL;
          if(support_avx())
	    return CPUTYPE_SANDYBRIDGE;
          else
	    return CPUTYPE_NEHALEM;
        }
      break;
      case 10: //family 6 exmodel 10
        switch (model) {
	  case 13: // Granite Rapids
	  if(support_amx_bf16())
	    return CPUTYPE_SAPPHIRERAPIDS;
	  if(support_avx512_bf16())
            return CPUTYPE_COOPERLAKE;	
          if(support_avx512())
            return CPUTYPE_SKYLAKEX;
          if(support_avx2())
            return CPUTYPE_HASWELL;
          if(support_avx())
            return CPUTYPE_SANDYBRIDGE;
          else
          return CPUTYPE_NEHALEM;
          case 5: // Comet Lake H and S
          case 6: // Comet Lake U
	  case 10: // Meteor Lake
            if(support_avx2())
              return CPUTYPE_HASWELL;
            if(support_avx())
              return CPUTYPE_SANDYBRIDGE;
            else
              return CPUTYPE_NEHALEM;
	  case 0: // Meteor Lake
          case 7: // Rocket Lake           
	    if(support_avx512())
              return CPUTYPE_SKYLAKEX;
            if(support_avx2())
              return CPUTYPE_HASWELL;
            if(support_avx())
	      return CPUTYPE_SANDYBRIDGE;
	    else
	    return CPUTYPE_NEHALEM;
        }
        break;
      case 11: //family 6 exmodel 11
        switch (model) {
          case 7: // Raptor Lake
          case 10:
          case 15:
	  case 14: // Alder Lake N
            if(support_avx2())
              return CPUTYPE_HASWELL;
	    if(support_avx())
	      return CPUTYPE_SANDYBRIDGE;
            else
	      return CPUTYPE_NEHALEM;
        }
        break;
      case 12: //family 6 exmodel 12
	switch (model) {
	  case 15:
	    if(support_avx512())
              return CPUTYPE_SAPPHIRERAPIDS;
            if(support_avx2())
              return CPUTYPE_HASWELL;
            if(support_avx())
	      return CPUTYPE_SANDYBRIDGE;
	    else
	    return CPUTYPE_NEHALEM;
	  }
	break;
      }
      break;    
    case 0x7:
      return CPUTYPE_ITANIUM;
    case 0xf:
      switch (exfamily) {
      case 0 :
	return CPUTYPE_PENTIUM4;
      case 1 :
	return CPUTYPE_ITANIUM;
      }
      break;
    }
    return CPUTYPE_INTEL_UNKNOWN;
  }

  if (vendor == VENDOR_AMD){
    switch (family) {
    case 0x4:
      return CPUTYPE_AMD5X86;
    case 0x5:
      return CPUTYPE_AMDK6;
    case 0x6:
#if defined(__x86_64__) || defined(__amd64__)
      return CPUTYPE_BARCELONA;
#else
      return CPUTYPE_ATHLON;
#endif
    case 0xf:
      switch (exfamily) {
      case  0:
      case  2:
	return CPUTYPE_OPTERON;
      case  1:
      case  3:
//      case  7:
//      case 10:
	return CPUTYPE_BARCELONA;
      case  5:
      case  7:		      
	return CPUTYPE_BOBCAT;
      case  6:
	switch (model) {
	case 1:
	  //AMD Bulldozer Opteron 6200 / Opteron 4200 / AMD FX-Series
	  if(support_avx())
	    return CPUTYPE_BULLDOZER;
	  else
	    return CPUTYPE_BARCELONA; //OS don't support AVX.
	case 2: //AMD Piledriver
	case 3: //AMD Richland
	  if(support_avx())
	    return CPUTYPE_PILEDRIVER;
	  else
	    return CPUTYPE_BARCELONA; //OS don't support AVX.
	case 5: // New EXCAVATOR CPUS
	  if(support_avx())
	    return CPUTYPE_EXCAVATOR;
	  else
	    return CPUTYPE_BARCELONA; //OS don't support AVX.
	case 0:
        case 8:
	  switch(exmodel){
	  case 1: //AMD Trinity
	    if(support_avx())
	      return CPUTYPE_PILEDRIVER;
	    else
	      return CPUTYPE_BARCELONA; //OS don't support AVX.
	  case 3:
	    if(support_avx())
	      return CPUTYPE_STEAMROLLER;
	    else
	      return CPUTYPE_BARCELONA; //OS don't support AVX.

	  case 6:
	    if(support_avx())
	      return CPUTYPE_EXCAVATOR;
	    else
	      return CPUTYPE_BARCELONA; //OS don't support AVX.
	  }
	  break;
	}
	break;
      case 8:
	switch (model) {
	case 1:
	  // AMD Ryzen
	case 8:
	  // AMD Ryzen2
	default:
	  // Matisse/Renoir and other recent Ryzen2		
	  if(support_avx())
#ifndef NO_AVX2
	    return CPUTYPE_ZEN;
#else
	    return CPUTYPE_SANDYBRIDGE; // Zen is closer in architecture to Sandy Bridge than to Excavator
#endif
	  else
	    return CPUTYPE_BARCELONA;
        }
      case 10: // Zen3/4
      case 11: // Zen5
#ifndef NO_AVX512
          if(support_avx512_bf16())
            return CPUTYPE_COOPERLAKE;
          if(support_avx512())
            return CPUTYPE_SKYLAKEX;
#endif
	if(support_avx())
#ifndef NO_AVX2
	    return CPUTYPE_ZEN;
#else
	    return CPUTYPE_SANDYBRIDGE; // Zen is closer in architecture to Sandy Bridge than to Excavator
#endif
	  else
	    return CPUTYPE_BARCELONA;	      
      }
      break;
    }
    return CPUTYPE_AMD_UNKNOWN;
  }

  if (vendor == VENDOR_HYGON){
    switch (family) {
    case 0xf:
      switch (exfamily) {
      case 9:
          //Hygon Dhyana
	  if(support_avx())
#ifndef NO_AVX2
	    return CPUTYPE_ZEN;
#else
	    return CPUTYPE_SANDYBRIDGE; // closer in architecture to Sandy Bridge than to Excavator
#endif
	  else
	    return CPUTYPE_BARCELONA;
        }
      break;
    }
    return CPUTYPE_HYGON_UNKNOWN;
  }

  if (vendor == VENDOR_CYRIX){
    switch (family) {
    case 0x4:
      return CPUTYPE_CYRIX5X86;
    case 0x5:
      return CPUTYPE_CYRIXM1;
    case 0x6:
      return CPUTYPE_CYRIXM2;
    }
    return CPUTYPE_CYRIX_UNKNOWN;
  }

  if (vendor == VENDOR_NEXGEN){
    switch (family) {
    case 0x5:
      return CPUTYPE_NEXGENNX586;
    }
    return CPUTYPE_NEXGEN_UNKNOWN;
  }

  if (vendor == VENDOR_CENTAUR){
    switch (family) {
    case 0x5:
      return CPUTYPE_CENTAURC6;
    case 0x6:
      if (model == 0xf && stepping < 0xe)
        return CPUTYPE_NANO;
      return CPUTYPE_NEHALEM;
    case 0x7:
      switch (exmodel) {
      case 5:
        if (support_avx2())
          return CPUTYPE_ZEN;
        else
          return CPUTYPE_DUNNINGTON;
      default:
        return CPUTYPE_NEHALEM;
      }
    default:
      if (family >= 0x8)
        return CPUTYPE_NEHALEM;
      else
        return CPUTYPE_VIAC3;
    }
  }

  if (vendor == VENDOR_ZHAOXIN){
    switch (family) {
      case 0x7:
        switch (exmodel) {
        case 5:
          if (support_avx2())
            return CPUTYPE_ZEN;
          else
            return CPUTYPE_DUNNINGTON;
        default:
          return CPUTYPE_NEHALEM;
        }
      default:
        return CPUTYPE_NEHALEM;
    }
  }

  if (vendor == VENDOR_RISE){
    switch (family) {
    case 0x5:
      return CPUTYPE_RISEMP6;
    }
    return CPUTYPE_RISE_UNKNOWN;
  }

  if (vendor == VENDOR_SIS){
    switch (family) {
    case 0x5:
      return CPUTYPE_SYS55X;
    }
    return CPUTYPE_SIS_UNKNOWN;
  }

  if (vendor == VENDOR_TRANSMETA){
    switch (family) {
    case 0x5:
      return CPUTYPE_CRUSOETM3X;
    }
    return CPUTYPE_TRANSMETA_UNKNOWN;
  }

  if (vendor == VENDOR_NSC){
    switch (family) {
    case 0x5:
      return CPUTYPE_NSGEODE;
    }
    return CPUTYPE_NSC_UNKNOWN;
  }

  return CPUTYPE_UNKNOWN;
}

