	if ($flags =~ /^\--exclude-libs/) {
	    $linker_L .= "-Wl,". $flags . " ";
	    $flags="";
	}


	if ($flags =~ /^\-rpath\%/) {
	    $flags =~ s/\%/\,/g;
	    $linker_L .= "-Wl,". $flags . " " ;
	}

	if ($flags =~ /^\-rpath-link\%/) {
	    $flags =~ s/\%/\,/g;
