	    && ($flags !~ /shell32/)
	    && ($flags !~ /omp/ || ($vendor !~ /PGI/ && $vendor !~ /FUJITSU/ && $flags =~ /omp/))
	    && ($flags !~ /[0-9]+/ || ($vendor == FUJITSU && $flags =~ /^-lfj90/))
		&& ($flags !~ /^\-l$/)
	    ) {
	    $linker_l .= $flags . " ";
	}

	if ( $flags =~ /quickfit.o/ && $vendor == NAG) {
	    $linker_l .= $flags . " ";
	}
	if ( $flags =~ /safefit.o/ && $vendor == NAG) {
	    $linker_l .= $flags . " ";
	}
	if ( $flags =~ /thsafe.o/ && $vendor == NAG) {
