// vite.config.js
import vue from '@vitejs/plugin-vue'
import { resolve } from 'path'
import mkcert from'vite-plugin-mkcert'

export default {
  base: "./",
  server: {
    https: true
  },
  resolve: {
    alias: {
      vue: '@vue/compat',
      "@": resolve(__dirname, "./src")
    }
  },

  plugins: [
    mkcert(),
    vue({
      template: {
        compilerOptions: {
          isCustomElement: tag => tag.includes('coral'),
          compatConfig: {
            MODE: 2
          }
        }
      }
    })
  ]
}



