THE SOFTWARE IS PROVIDED "AS IS", WITHOUT WARRANTY OF ANY KIND, EXPRESS OR
IMPLIED, INCLUDING BUT NOT LIMITED TO THE WARRANTIES OF MERCHANTABILITY,
FITNESS FOR A PARTICULAR PURPOSE AND NONINFRINGEMENT. IN NO EVENT SHALL THE
AUTHORS OR COPYRIGHT HOLDERS BE LIABLE FOR ANY CLAIM, DAMAGES OR OTHER
LIABILITY, WHETHER IN AN ACTION OF CONTRACT, TORT OR OTHERWISE, ARISING FROM,
OUT OF OR IN CONNECTION WITH THE SOFTWARE OR THE USE OR OTHER DEALINGS IN THE
SOFTWARE.

*/
@charset "UTF-8";
/* Variables */
body {
  --font-editor-theme:-apple-system,BlinkMacSystemFont,"Segoe UI",Roboto,Inter,Ubuntu,sans-serif;
  --font-editor:var(--font-editor-override), var(--font-text-override), var(--font-editor-theme);
}

body {
  /* Blockquotes */
  --blockquote-style:normal;
  --blockquote-color: var(--text-muted);
  --blockquote-border-thickness: 1px;
  --blockquote-border-color: var(--quote-opening-modifier);
  /* Embeds */
  --embed-block-shadow-hover: none;
  /* Font sizes */
  --font-ui-smaller: 11px;
  /* Font weights */
  --normal-weight: 400;
  /* Headings */
  --inline-title-margin-bottom: 1rem;
  --h1-size: 1.125em;
  --h2-size: 1.05em;
  --h3-size: 1em;
  --h4-size: 0.90em;
  --h5-size: 0.85em;
  --h6-size: 0.85em;
  --h1-weight: 600;
  --h2-weight: 600;
  --h3-weight: 500;
  --h4-weight: 500;
  --h5-weight: 500;
  --h6-weight: 400;
  --h1-variant: normal;
  --h2-variant: normal;
  --h3-variant: normal;
  --h4-variant: normal;
  --h5-variant: small-caps;
  --h6-variant: small-caps;
  --h1-style: normal;
  --h2-style: normal;
  --h3-style: normal;
  --h4-style: normal;
  --h5-style: normal;
  --h6-style: normal;
  /* Line widths */
  --line-width: 40rem;
  --line-height: 1.5;
  --line-height-normal: var(--line-height);
  --max-width: 88%;
  --max-col-width: 18em;
  /* Icons */
  --icon-muted: 0.5;
  /* Quotes and transclusions */
  --nested-padding:1.1em;
  /* Lists */
  --folding-offset: 32px;
  --list-edit-offset: 0.5em;
  --list-indent: 2em;
  --list-spacing: 0.075em;
  --input-height: 32px;
  --header-height: 40px;
  /* Metadata */
  --metadata-label-width-multiplier: 9;
  --metadata-label-width: calc(var(--font-adaptive-small) * var(--metadata-label-width-multiplier));
  --metadata-label-font-size: var(--font-adaptive-small);
  --metadata-input-font-size: var(--font-adaptive-small);
  /* Mobile sidebars */
  --mobile-left-sidebar-width: 280pt;
  --mobile-right-sidebar-width: 240pt;
  --top-left-padding-y:0px;
  /* Image opacity in dark mode */
  --image-muted:0.7;
  --image-radius: 4px;
  /* Spacing */
  --heading-spacing: 2em;
  --p-spacing: 1.75rem;
  --border-width: 1px;
  --table-border-width: var(--border-width);
  --table-selection: var(--text-selection);
  --table-selection-border-color: var(--text-accent);
  --table-selection-border-width: 0px;
  --table-selection-border-radius: 0px;
  --table-drag-handle-background-active: var(--text-selection);
  --table-drag-handle-color-active: var(--text-accent);
  --table-add-button-border-width: 0px;
  /* Readable line length off */
  --file-margins: var(--size-4-2) var(--size-4-12);
}

.mod-macos {
  --top-left-padding-y:24px;
}

.is-phone {
  /* Metadata */
  --metadata-label-font-size: var(--font-adaptive-smaller);
  --metadata-input-font-size: var(--font-adaptive-smaller);
}

@media only screen and (-webkit-min-device-pixel-ratio: 2), only screen and (min-device-pixel-ratio: 2) {
  .is-phone {
    --border-width: 0.75px;
  }
}
/* Dynamic colors

Most colors are driven from the following values, meaning that 
the backgrounds, borders, and various shades are 
automatically generated.

- Base color is used for the backgrounds, text and borders.
- Accent color is used for links and some interactive elements.

The colors use HSL (hue, saturation, lightness)

- Hue (0-360 degrees):0 is red, 120 is green, and 240 is blue
- Saturation (0-100%):0% is desaturated, 100% is full saturation
- Lightness (0-100%):0% is black, 100% is white

*/
body {
  --base-h: 0; /* Base hue */
  --base-s: 0%; /* Base saturation */
  --base-l: 96%; /* Base lightness - 100 is white */
  --accent-h: 201; /* Accent hue */
  --accent-s: 17%; /* Accent saturation */
  --accent-l: 50%; /* Accent lightness */
}

.theme-dark,
.theme-light {
  --color-red-rgb: 208,66,85;
  --color-orange-rgb: 213,118,63;
  --color-yellow-rgb: 229,181,103;
  --color-green-rgb: 168,195,115;
  --color-cyan-rgb: 115,187,178;
  --color-blue-rgb: 108,153,187;
  --color-purple-rgb: 158,134,200;
  --color-pink-rgb: 176,82,121;
  --color-red: #d04255;
  --color-orange: #d5763f;
  --color-yellow: #e5b567;
  --color-green: #a8c373;
  --color-cyan: #73bbb2;
  --color-blue: #6c99bb;
  --color-purple: #9e86c8;
  --color-pink: #b05279;
}

.theme-light,
.theme-light.minimal-default-light,
body .excalidraw {
  --bg1:white;
  --bg2:
  hsl(
  	var(--base-h),
  	var(--base-s),
  	var(--base-l)
  	);
  --bg3:
  hsla(
  	var(--base-h),
  	var(--base-s),
  	calc(var(--base-l) - 50%),
  	0.12
  	);
  --ui1:
  hsl(
  	var(--base-h),
  	var(--base-s),
  	calc(var(--base-l) - 6%)
  	);
  --ui2:
  hsl(
  	var(--base-h),
  	var(--base-s),
  	calc(var(--base-l) - 12%)
  	);
  --ui3:
  hsl(
  	var(--base-h),
  	var(--base-s),
  	calc(var(--base-l) - 20%)
  	);
  --tx1:
  hsl(
  	var(--base-h),
  	var(--base-s),
  	calc(var(--base-l) - 90%)
  	);
  --tx2:
  hsl(
  	var(--base-h),
  	calc(var(--base-s) - 20%),
  	calc(var(--base-l) - 50%)
  	);
  --tx3:
  hsl(
  	var(--base-h),
  	calc(var(--base-s) - 10%),
  	calc(var(--base-l) - 25%)
  	);
  --tx4:
  hsl(
  	var(--base-h),
  	calc(var(--base-s) - 10%),
  	calc(var(--base-l) - 60%)
  	);
  --ax1:
  hsl(
  	var(--accent-h),
  	var(--accent-s),
  	var(--accent-l)
  	);
  --ax2:
  hsl(
  	var(--accent-h),
  	var(--accent-s),
  	calc(var(--accent-l) - 8%)
  	);
  --ax3:
  hsl(
  	var(--accent-h),
  	var(--accent-s),
  	calc(var(--accent-l) + 6%)
  	);
  --hl1:
  hsla(
  	var(--accent-h),
  	50%,
  	calc(var(--base-l) - 20%),
  	30%
  	);
  --hl2:rgba(255, 225, 0, 0.5);
  --sp1: white;
}

.theme-light.minimal-light-contrast .titlebar,
.theme-light.minimal-light-contrast.minimal-status-off .status-bar,
.theme-dark,
.theme-dark.minimal-default-dark,
.excalidraw.theme--dark {
  --accent-l:60%;
  --base-l:15%;
  --bg1:
  hsl(
  	var(--base-h),
  	var(--base-s),
  	var(--base-l)
  	);
  --bg2:
  hsl(
  	var(--base-h),
  	var(--base-s),
  	calc(var(--base-l) - 2%)
  	);
  --bg3:
  hsla(
  	var(--base-h),
  	var(--base-s),
  	calc(var(--base-l) + 40%),
  	0.12
  	);
  --ui1:
  hsl(
  	var(--base-h),
  	var(--base-s),
  	calc(var(--base-l) + 6%)
  	);
  --ui2:
  hsl(
  	var(--base-h),
  	var(--base-s),
  	calc(var(--base-l) + 12%)
  	);
  --ui3:
  hsl(
  	var(--base-h),
  	var(--base-s),
  	calc(var(--base-l) + 20%)
  	);
  --tx1:
  hsl(
  	var(--base-h),
  	calc(var(--base-s) - 10%),
  	calc(var(--base-l) + 67%)
  	);
  --tx2:
  hsl(
  	var(--base-h),
  	calc(var(--base-s) - 20%),
  	calc(var(--base-l) + 45%)
  	);
  --tx3:
  hsl(
  	var(--base-h),
  	calc(var(--base-s) - 10%),
  	calc(var(--base-l) + 20%)
  	);
  --tx4:
  hsl(
  	var(--base-h),
  	calc(var(--base-s) - 10%),
  	calc(var(--base-l) + 50%)
  	);
  --ax1:
  hsl(
  	var(--accent-h),
  	var(--accent-s),
  	var(--accent-l)
  	);
  --ax2:
  hsl(
  	var(--accent-h),
  	var(--accent-s),
  	calc(var(--accent-l) + 8%)
  	);
  --ax3:
  hsl(
  	var(--accent-h),
  	var(--accent-s),
  	calc(var(--accent-l) - 5%)
  	);
  --hl1:
  hsla(
  	var(--accent-h),
  	50%,
  	40%,
  	30%
  	);
  --hl2:rgba(255, 177, 80, 0.3);
  --sp1: white;
}

.theme-light.minimal-light-white {
  --background-primary: white;
  --background-secondary: white;
  --background-secondary-alt: white;
  --ribbon-background: white;
  --titlebar-background: white;
  --bg1: white;
}

.theme-dark.minimal-dark-black {
  --base-d:0%;
  --titlebar-background: black;
  --background-primary: black;
  --background-secondary: black;
  --background-secondary-alt: black;
  --ribbon-background: black;
  --background-modifier-hover:
  hsl(
  	var(--base-h),
  	var(--base-s),
  	calc(var(--base-d) + 10%)) ;
  --tx1:hsl(
  	var(--base-h),
  	var(--base-s),
  	calc(var(--base-d) + 75%)
  	);
  --tx2:hsl(
  	var(--base-h),
  	var(--base-s),
  	calc(var(--base-d) + 50%)
  	);
  --tx3:hsl(
  	var(--base-h),
  	var(--base-s),
  	calc(var(--base-d) + 25%)
  	);
  --ui1:hsl(
  	var(--base-h),
  	var(--base-s),
  	calc(var(--base-d) + 12%)
  	);
  --ui2:hsl(
  	var(--base-h),
  	var(--base-s),
  	calc(var(--base-d) + 20%)
  	);
  --ui3:hsl(
  	var(--base-h),
  	var(--base-s),
  	calc(var(--base-d) + 30%)
  	);
}

/* Map colors to semantic Obsidian names */
.theme-light {
  --mono100: black;
  --mono0: white;
}

.theme-dark {
  --mono100: white;
  --mono0: black;
}

.theme-light.minimal-light-contrast .titlebar,
.theme-light.minimal-light-contrast.minimal-status-off .status-bar,
.theme-light.minimal-light-contrast.is-mobile .workspace-drawer.mod-left,
.theme-dark,
.theme-light {
  --background-modifier-accent: var(--ax3);
  --background-modifier-border-focus: var(--ui3);
  --background-modifier-border-hover: var(--ui2);
  --background-modifier-border: var(--ui1);
  --mobile-sidebar-background: var(--bg1);
  --background-modifier-form-field-highlighted: var(--bg1);
  --background-modifier-form-field: var(--bg1);
  --background-modifier-success: var(--color-green);
  --background-modifier-hover: var(--bg3);
  --background-modifier-active-hover: var(--bg3);
  --background-primary: var(--bg1);
  --background-primary-alt: var(--bg2);
  --background-secondary: var(--bg2);
  --background-secondary-alt: var(--bg1);
  --background-table-rows: var(--bg2);
  --checkbox-color: var(--ax3);
  --code-normal: var(--tx1);
  --divider-color: var(--ui1);
  --frame-divider-color: var(--ui1);
  --icon-color-active: var(--tx1);
  --icon-color-focused: var(--tx1);
  --icon-color-hover: var(--tx2);
  --icon-color: var(--tx2);
  --icon-hex: var(--mono0);
  --interactive-accent-hover: var(--ax1);
  --interactive-accent: var(--ax3);
  --interactive-hover: var(--ui1);
  --list-marker-color: var(--tx3);
  --nav-item-background-active: var(--bg3);
  --nav-item-background-hover: var(--bg3);
  --nav-item-color: var(--tx2);
  --nav-item-color-active: var(--tx1);
  --nav-item-color-hover: var(--tx1);
  --nav-item-color-selected: var(--tx1);
  --nav-collapse-icon-color: var(--tx2);
  --nav-collapse-icon-color-collapsed: var(--tx2);
  --nav-indentation-guide-color: var(--ui1);
  --prompt-border-color: var(--ui3);
  --quote-opening-modifier: var(--ui2);
  --ribbon-background: var(--bg2);
  --scrollbar-active-thumb-bg: var(--ui3);
  --scrollbar-bg: transparent;
  --scrollbar-thumb-bg: var(--ui1);
  --search-result-background: var(--bg1);
  --tab-text-color-focused-active: var(--tx1);
  --tab-outline-color: var(--ui1);
  --text-accent-hover: var(--ax2);
  --text-accent: var(--ax1);
  --text-blockquote: var(--tx2);
  --text-bold: var(--tx1);
  --text-code: var(--tx4);
  --text-error: var(--color-red);
  --text-faint: var(--tx3);
  --text-highlight-bg: var(--hl2);
  --text-italic: var(--tx1);
  --text-muted: var(--tx2);
  --text-normal: var(--tx1);
  --text-on-accent: var(--sp1);
  --text-selection: var(--hl1);
  --text-formatting: var(--tx3);
  --title-color-inactive: var(--tx2);
  --title-color: var(--tx1);
  --titlebar-background: var(--bg2);
  --titlebar-background-focused: var(--bg2);
  --titlebar-text-color-focused: var(--tx1);
  --vault-profile-color: var(--tx1);
  --vault-profile-color-hover: var(--tx1);
  --workspace-background-translucent: hsla(var(--base-h),var(--base-s), var(--base-l), 0.7);
}

.theme-dark .view-actions,
.theme-light .view-actions {
  --icon-color-active: var(--ax1);
}

.theme-light.minimal-light-contrast {
  --workspace-background-translucent: rgba(0,0,0,0.6);
}

.theme-light.minimal-light-contrast .theme-dark {
  --tab-container-background: var(--bg2);
  --ribbon-background-collapsed: var(--bg2);
}

.theme-light {
  --interactive-normal: var(--bg1);
  --interactive-accent-rgb:220, 220, 220;
  --active-line-bg: rgba(0,0,0,0.035);
  --background-modifier-cover:hsla(var(--base-h),calc(var(--base-s) - 70%),calc(var(--base-l) - 20%),0.5);
  --text-highlight-bg-active: rgba(0, 0, 0, 0.1);
  /* Errors */
  --background-modifier-error: rgba(255,0,0,0.14);
  --background-modifier-error-hover: rgba(255,0,0,0.08);
  /* Shadows */
  --shadow-color: rgba(0, 0, 0, 0.1);
  --btn-shadow-color: rgba(0, 0, 0, 0.05);
}

.theme-dark {
  --interactive-normal: var(--bg3);
  --interactive-accent-rgb: 66, 66, 66;
  --active-line-bg: rgba(255,255,255,0.04);
  --background-modifier-cover: hsla(var(--base-h),var(--base-s), calc(var(--base-l) - 12%), 0.5);
  --text-highlight-bg-active: rgba(255, 255, 255, 0.1);
  /* Errors */
  --background-modifier-error: rgba(255,20,20,0.12);
  --background-modifier-error-hover: rgba(255,20,20,0.18);
  /* Shadows */
  --background-modifier-box-shadow: rgba(0, 0, 0, 0.3);
  --shadow-color: rgba(0, 0, 0, 0.3);
  --btn-shadow-color: rgba(0, 0, 0, 0.2);
  --modal-border-color: var(--ui2);
}

.theme-light.minimal-light-white {
  --background-table-rows: var(--bg2);
}

.theme-light.minimal-light-tonal {
  --background-primary: var(--bg2);
  --background-primary-alt: var(--bg3);
  --background-table-rows: var(--bg3);
}

.theme-dark.minimal-dark-tonal {
  --ribbon-background: var(--bg1);
  --background-secondary: var(--bg1);
  --background-table-rows: var(--bg3);
}

.theme-dark.minimal-dark-black {
  --background-primary-alt: var(--bg3);
  --background-table-rows: var(--bg3);
  --modal-border: var(--ui2);
  --active-line-bg: rgba(255,255,255,0.085);
  --background-modifier-form-field: var(--bg3);
  --background-modifier-cover:hsla(var(--base-h),var(--base-s),calc(var(--base-d) + 8%),0.9);
  --background-modifier-box-shadow: rgba(0, 0, 0, 1);
}

/* Desktop font sizes */
body {
  --font-adaptive-normal:var(--font-text-size,var(--editor-font-size));
  --font-adaptive-small: calc(var(--font-ui-small) * 1.07);
  --font-adaptive-smaller: var(--font-ui-small);
  --font-adaptive-smallest: var(--font-ui-smaller);
  --line-width-wide:calc(var(--line-width) + 12.5%);
  --font-code:calc(var(--font-adaptive-normal) * 0.9);
  --table-text-size:calc(var(--font-adaptive-normal) * 0.875);
}

/* Obsidian */
/* Block width snippet */
.minimal-dev-block-width {
  /* Green — Folding offset width */
  /* Red — Max width */
  /* Orange — Wide line width*/
  /* Blue — Normal line width */
}
.minimal-dev-block-width .mod-root .workspace-leaf-content:after {
  display: flex;
  align-items: flex-end;
  content: " pane ";
  font-size: 12px;
  color: gray;
  font-family: var(--font-monospace);
  width: 100%;
  max-width: 100%;
  height: 100vh;
  top: 0;
