/* Obsidian app */
/* Editor mode (CodeMirror 6 Live Preview) */
/* Fix strange Obsidian ghost textearea bug on right click */
.CodeMirror-wrap > div > textarea {
  opacity: 0;
}

.markdown-source-view.mod-cm6 hr {
  border-width: 2px;
}

.cm-editor .cm-content {
  padding-top: 0.5em;
}

.markdown-source-view {
