  max-height: 500px;
  padding-top: 4px;
  transition: opacity 100ms ease-out, max-height 200ms ease-out, padding-top 200ms ease-in-out;
}
.hide-settings-desc .setting-item-description {
  opacity: 0;
  max-height: 0;
  padding-top: 0;
