  font-size: var(--table-text-size);
  border-radius: var(--radius-m);
  padding: 15px 0;
  justify-content: flex-start;
}
body div.dataview-error-box p {
  margin-block-start: 0;
  margin-block-end: 0;
  color: var(--text-faint);
}

