}

.git-view-body .file-view .tools .type {
  display: none !important;
}

.git-view-body .file-view .tools {
  opacity: 0;
  transition: opacity 0.1s;
}

.git-view-body .file-view main:hover > .tools {
  opacity: 1;
}

.git-view-body .staged {
  margin-bottom: 12px;
}

.git-view-body .opener.open {
