        title: Disable line wrap
        description: Turn off line wrapping in table cells globally
        type: class-toggle
        default: false
    -
        id: row-hover
        title: Highlight active row
        description: Highlight rows on hover
        type: class-toggle
        default: false
    - 
        id: row-color-hover
        title: Active row background
        description: Background color for hovered tables rows
        type: variable-themed-color
        format: hex
        opacity: true
        default-light: '#'
        default-dark: '#'
    -
        id: tags
        title: Tags
        type: heading
        level: 2
        collapsed: true
    -
        id: minimal-unstyled-tags
        title: Plain tags
        description: Tags will render as normal text, overrides settings below
        type: class-toggle
        default: false
    -
        id: tag-radius
        title: Tag shape
        type: variable-select
        default: 14px
        options:
            - 
                label: Pill
                value: 14px
            - 
                label: Rounded
                value: 4px
            - 
                label: Square
                value: 0px
    -
        id: tag-border-width
        title: Tag border width
        type: variable-select
        default: 1px
        options:
            - 
                label: None
                value: 0
            - 
                label: Thin
                value: 1px
            - 
                label: Thick
                value: 2px
    - 
        id: tag-color
        title: Tag text color
        type: variable-themed-color
        opacity: true
        format: hex
        default-light: '#'
        default-dark: '#'
    - 
        id: tag-bg
        title: Tag background color
        type: variable-themed-color
        opacity: true
        format: hex
        default-light: '#'
        default-dark: '#'
    - 
        id: tag-bg2
        title: Tag background color (hover)
        type: variable-themed-color
        opacity: true
        format: hex
        default-light: '#'
        default-dark: '#'
    -
        id: text
        title: Text
        type: heading
        level: 2
        collapsed: true
    - 
        id: tx1
        title: Normal text color
        type: variable-themed-color
        description: Primary text color used by default across all elements
        opacity: true
        format: hex
        default-light: '#'
        default-dark: '#'
    - 
        id: hl1
        title: Selected text background
        type: variable-themed-color
        opacity: true
        format: hex
        default-light: '#'
        default-dark: '#'
    - 
        id: hl2
        title: Highlighted text background
        type: variable-themed-color
        opacity: true
        format: hex
        default-light: '#'
        default-dark: '#'
    - 
        id: tx2
        title: Muted text color
        description: Secondary text such as sidebar note titles and table headings
        type: variable-themed-color
        opacity: true
        format: hex
        default-light: '#'
        default-dark: '#'
    - 
        id: tx3
        title: Faint text color
        description: tertiary text such as input placeholders, empty checkboxes, and disabled statuses
        type: variable-themed-color
        opacity: true
        format: hex
        default-light: '#'
        default-dark: '#'
    - 
        id: text-italic
        title: Italic text color
        type: variable-themed-color
        format: hex
        default-light: '#'
        default-dark: '#'
    - 
        id: text-bold
        title: Bold text color
        type: variable-themed-color
        format: hex
        default-light: '#'
        default-dark: '#'
    - 
        id: bold-weight
        title: Bold text weight
        type: variable-number-slider
        default: 600
        min: 100
        max: 900
        step: 100
    - 
        id: spacing-p
        title: Paragraph spacing
        description: Space between paragraphs in reading mode
        type: variable-text
        default: 0.75em
    -
        id: titlebar
        title: Title bar
        type: heading
        level: 2
        collapsed: true
    - 
        id: title-alignment
        title: Title alignment
        description: Position of the text within the title bar
        type: class-select
        allowEmpty: false
        default: title-align-body
        options:
            - 
                label: Text body
                value: title-align-body
            - 
                label: Left
                value: title-align-left
            -
                label: Center
                value: title-align-center
    -
        id: show-grabber
        title: Always show grabber icon
        description: Make the dragging handle always visible in the top left corner of a pane
        type: class-toggle
        default: false
    - 
        id: header-height
        title: Title bar height
        type: variable-text
        default: 42px
    - 
        id: title-size
        title: Title font size
        description: Accepts any CSS font-size value
        type: variable-text
        default: 1.1em
    - 
        id: title-weight
        title: Title font weight
        type: variable-number-slider
        default: 600
        min: 100
        max: 900
        step: 100
    - 
        id: title-color
        title: Title text color (active)
        type: variable-themed-color
        format: hex
        default-light: '#'
        default-dark: '#'
    - 
        id: title-color-inactive
        title: Title text color (inactive)
        type: variable-themed-color
        format: hex
        default-light: '#'
        default-dark: '#'
    -
        id: translucency
        title: Translucency
        type: heading
        level: 2
        collapsed: true
    -
        id: bg-translucency-light
        title: Translucency (light mode)
        description: Sidebar translucency in light mode. Requires turning on "Translucent window" in Appearance settings, and "Translucent sidebar" in Minimal settings.
        type: variable-number-slider
        default: 0.7
        min: 0
        max: 1
        step: 0.05
    -
        id: bg-translucency-dark
        title: Translucency (dark mode)
        description: Sidebar translucency in dark mode
        type: variable-number-slider
        default: 0.85
        min: 0
        max: 1
        step: 0.05

*/

