var path = require("path");

module.exports = function (grunt) {
    grunt.loadNpmTasks("grunt-contrib-clean");
    grunt.loadNpmTasks("grunt-contrib-jshint");
    grunt.loadNpmTasks("grunt-contrib-cssmin");
    grunt.loadNpmTasks("grunt-contrib-concat");
    grunt.loadNpmTasks("grunt-mochaccino");
    grunt.loadNpmTasks("grunt-closure-compiler");
    grunt.loadNpmTasks("grunt-banner");
    grunt.loadNpmTasks('grunt-karma');
    grunt.loadNpmTasks('grunt-contrib-less');
    grunt.loadNpmTasks('grunt-contrib-watch');


    grunt.initConfig({
        pkg: grunt.file.readJSON('package.json'),
        clean: [ "build/cov" ],

        // see .jshintrc file for the options;
        // options are explained at http://www.jshint.com/docs/config/
        jshint: {
            options: {
                jshintrc: ".jshintrc"
            },

            core: [ "src/*.js" ],

        },
        karma: {
            unit: {
                  configFile: 'karma.conf.js'
                    }
        },

        concat: {
            afui:{
                files: {
                    "build/appframework.ui.js": [
                        "src/af.shim.js",
                        "src/af.ui.js",
                        "src/af.actionsheet.js",
                        "src/af.grower.js",
                        "src/af.touchEvents.js",
                        "src/af.animateheader.js",
                        "src/af.popup.js",
                        "src/af.animation.js",
                        "src/af.splashscreen.js",
                        "src/af.drawer.js",
                        "src/af.swipereveal.js",
                        "src/af.desktopBrowsers.js",
                        "src/af.toast.js",
                        "src/af.lockscreen.js"
                    ]
                }
            },
            less: {
                files: {
                    "build/af.ui.less":[
                        "src/less/main.less",
                        "src/less/anim2.less",
                        "src/less/animation.less",
                        "src/less/*.less"
                    ]
                }
            },
            lessBase: {
                files: {
                    "./build/af.ui.base.less": [
                    "src/less/main.less",
                    "src/less/anim2.less",
                    "src/less/animation.less",
                    "src/less/appframework.less",
                    "src/less/af.actionsheet.less",
                    "src/less/af.popup.less",
                    "src/less/af.splashscreen.less",
                    "src/less/af.swipereveal.less",
                    "src/less/af.toast.less",
                    "src/less/badges.less",
                    "src/less/buttons.less",
                    "src/less/forms.less",
                    "src/less/grid.less",
                    "src/less/lists.less",
                    "src/less/splitview.less",
                    "src/less/af.lockscreen.less"
                    ]
                }
            }
        },
        "closure-compiler": {
            "appframework-ui": {
                closurePath: "../closure/",
                js: ["build/appframework.ui.js"],
                jsOutputFile: "build/appframework.ui.min.js",
                options: {
                },
                maxBuffer: 500,
                noreport:true
            },
        },
        usebanner: {
            taskName: {
                options: {
                    position: "top",
                    banner: "/*! <%= pkg.name %> - v<%= pkg.version %> - "+
                        "<%= grunt.template.today('yyyy-mm-dd') %> */\n",
                    linebreak: true
                },
                files: {
                    src: [ "build/*.js","build/*.js","build/css/*.css" ]
                }
            }
        },
        less: {
            development: {
                options: {
                    paths: ["./src/less"],
                    yuicompress: false
                },
                files: {
                    "./build/af.ui.css": "./src/less/*.less"
                }
            },
            base: {
                options: {
                    paths: ["./src/less"],
                    yuicompress: false
                },
                files: {
                    "./build/af.ui.base.css": [
                    "src/less/main.less",
                    "src/less/anim2.less",
                    "src/less/animation.less",
                    "src/less/appframework.less",
                    "src/less/af.actionsheet.less",
                    "src/less/af.popup.less",
                    "src/less/af.splashscreen.less",
                    "src/less/af.swipereveal.less",
                    "src/less/af.toast.less",
                    "src/less/badges.less",
                    "src/less/buttons.less",
                    "src/less/forms.less",
                    "src/less/grid.less",
                    "src/less/lists.less",
                    "src/less/splitview.less",
                    "src/less/af.lockscreen.less"
                    ]
                }
            }
        },
        watch: {
            files: "./src/less/*.less",
            tasks: ["less"]
        }
    });


    grunt.registerTask("default", [
        "jshint",
        "test",
        "clean",
        "closure-compiler",
        "usebanner",
        "watch"
    ]);

    grunt.registerTask("rebuild" , ["less","concat","closure-compiler","usebanner"]);
    grunt.registerTask("hint" , ["jshint"]);
    grunt.registerTask("test" , ["karma"]);

};

module.exports = function (karma) {
    karma.set({

// base path, that will be used to resolve files and exclude
        basePath: './',

        frameworks: ['mocha'],
        preprocessors: {
            'test/fixtures/*.html': ['html2js'],
            '**/src/*.js': 'coverage'
        },

// list of files / patterns to load in the browser
        files: [
            {pattern: 'node_modules/chai/chai.js', include: true},
            {pattern: '3rdparty/jquery.min.js', served: true, included: true},
            {pattern: 'build/appframework.ui.js',served: true, included: true},
            {pattern: 'test/chai.helper.js', include: true},
            'build/af.ui.css',
            'test/fixtures/*.html',
            'test/*.test.js'
            /*'test/drawer.test.js'*/

        ],

// list of files to exclude
        exclude: [
            'karma.conf.js'
        ],

// use dots reporter, as travis terminal does not support escaping sequences
// possible values: 'dots', 'progress', 'junit', 'teamcity'
// CLI --reporters progress
        reporters: ['progress', 'coverage'],

//Code Coverage options. report type available:
//- html (default)
//- lcov (lcov and html)
//- lcovonly
//- text (standard output)
//- text-summary (standard output)
//- cobertura (xml format supported by Jenkins)
        coverageReporter: {
            // cf. http://gotwarlost.github.com/istanbul/public/apidocs/
            type: 'html',
            dir: 'coverage/'
        },


// web server port
        port: 9876,


// cli runner port
        runnerPort: 9100,


// enable / disable colors in the output (reporters and logs)
        colors: true,


// level of logging
// possible values: LOG_DISABLE || LOG_ERROR || LOG_WARN || LOG_INFO || LOG_DEBUG
        //logLevel: LOG_DEBUG,


// enable / disable watching file and executing tests whenever any file changes
        autoWatch: false,


// Start these browsers, currently available:
// - Chrome
// - ChromeCanary
// - Firefox
// - Opera
// - Safari (only Mac)
// - PhantomJS
// - IE (only Windows)
// CLI --browsers Chrome,Firefox,Safari
        browsers: ['Chrome'],


// If browser does not capture in given timeout [ms], kill it
        captureTimeout: 6000,


// Continuous Integration mode
// if true, it capture browsers, run tests and exit
        singleRun: true,


        plugins: [
            'karma-mocha',
            'karma-chrome-launcher',
            'karma-coverage',
            'karma-htmlfile-reporter',
            'karma-html2js-preprocessor'
        ]
    });
}
