{
  "env": {
    "node": true,
    "es2021": true,
    "jest": true,
    "cypress/globals": true
  },
  "extends": ["eslint:recommended", "prettier", "plugin:jest/recommended"],
  "parserOptions": {
    "ecmaVersion": "latest"
  },
  "rules": {
    "cypress/no-assigning-return-values": "error",
    "cypress/no-unnecessary-waiting": "error",
    "cypress/assertion-before-screenshot": "warn",
    "cypress/no-force": "warn",
    "cypress/no-async-tests": "error",
    "cypress/no-pause": "error",
    "jest/no-disabled-tests": "warn",
    "jest/no-focused-tests": "error",
    "jest/no-identical-title": "off",
    "jest/prefer-to-have-length": "warn",
    "jest/valid-expect": "error",
    "jest/expect-expect": "off"
  },
  "globals": {
    "jest": true
  },
  "plugins": ["jest", "cypress"]
}

const { defineConfig } = require('cypress')

module.exports = defineConfig({
  chromeWebSecurity: false,
  screenshotsFolder: 'spec/end_to_end_tests/reports/screenshots',
  reporter: 'mochawesome',
  reporterOptions: {
    reportDir: 'spec/end_to_end_tests/reports',
    reportFilename: 'results',
    quiet: 'true',
    json: 'false',
  },
  watchForFileChanges: false,
  defaultCommandTimeout: 30000,
  pageLoadTimeout: 60000,
  viewportHeight: 900,
  viewportWidth: 1440,
  e2e: {
    specPattern: 'spec/end_to_end_tests/specs/**/*.cy.{js,jsx,ts,tsx}',
    supportFile: false,
  },
  video: false,
})

# server block for static file serving
server {
  listen 80;
  location / {
    root /opt/build-your-own-radar;
    index index.html;
  }

  # nginx default error page for 50x errors
  error_page 500 502 503 504 /50x.html;
  location = /50x.html {
    root /usr/share/nginx/html;
  }

  location ~ /files/ {
    root /opt/build-your-own-radar;
    autoindex on;
    default_type text/plain;
    add_header 'Access-Control-Allow-Origin' '*';
  }
}

module.exports = {
  clearMocks: true,
  collectCoverageFrom: ['src/**/*.js'],
  testEnvironment: 'jsdom',
  transform: {
    '^.+\\.[jt]sx?$': 'babel-jest',
    '.+\\.(css|styl|less|sass|scss)$': 'jest-css-modules-transform',
  },
  testMatch: ['**/spec/**/*-spec.js'],
  coverageThreshold: {
    global: {
      statements: 23.95,
      branches: 20.08,
      functions: 28.98,
      lines: 24.09,
    },
  },
}

