{
    "$schema": "http://json-schema.org/draft-07/schema#",
    "type": "object",

    "patternProperties": {
        "^[a-z-]*$": {
            "$ref": "#/definitions/font"
        }
    },

    "definitions": {
        "font": {
            "type": "object",
            "properties": {
                "alias": {
                    "type": "string",
                    "pattern": "^[a-z][a-z0-9-]+$"
                },
                "name": { "type": "string" },
                "author": { "type": "string" },
                "description": { "type": "string" },
                "year": { "type": "integer" },
                "website": {
                    "type": "string",
                    "format": "uri"
                },
                "license": {
                    "type": "string",
                    "enum": [
                        "commercial",
                        "Apache",
                        "Bitstream Vera",
                        "BSD-2-Clause",
                        "CC BY-SA 4.0",
                        "CC BY-ND 3.0",
                        "freeware",
                        "GNU GPL",
                        "GUST font license",
                        "Luxi License",
                        "MIT",
                        "none",
                        "public domain",
                        "SIL OFL",
                        "Ubuntu Font Licence",
                        "WTFPL"
                    ]
                },
                "ligatures": { "type": "boolean" },
                "style": {
                    "type": "string",
                    "enum": ["serif", "sans"]
                },
                "rendering": {
                    "type": "string",
                    "enum": ["bitmap", "vector"]
                },
                "bitmap size": {
                    "type": "number"
                },
                "zerostyle": {
                    "type": "string",
                    "enum": ["empty", "slashed", "dotted"]
                },
                "variants": {
                    "oneOf": [
                        {
                            "type": "array",
                            "items": {
                                "type": "string",
                                "pattern": "^([a-z-]+|\\d00)$"
                            },
                            "minItems": 1
                        },
                        {
                            "type": "string"
                        }
                    ]
                }
            },
            "additionalProperties": false,
            "required": [
                "author",
                "description",
                "license",
                "ligatures",
                "name",
                "rendering",
                "style",
                "website",
                "year"
            ]
        }
    }
}

@font-face {
    font-family: 'ui-font';
    font-weight: normal;
    font-style: normal;
    src: url('merriweather/MerriweatherSans-Light.ttf');
}
@font-face {
    font-family: 'ui-font';
    font-weight: bold;
    font-style: normal;
    src: url('merriweather/MerriweatherSans-Bold.ttf');
}
@font-face {
    font-family: 'ui-font';
    font-weight: normal;
    font-style: italic;
    src: url('merriweather/MerriweatherSans-Italic.ttf');
}
@font-face {
    font-family: 'ui-font';
    font-weight: bold;
    font-style: italic;
    src: url('merriweather/MerriweatherSans-BoldItalic.ttf');
}

@font-face {
    font-family: 'ui-font-serif';
    font-weight: normal;
    font-style: normal;
    src: url('merriweather/Merriweather-Light.ttf');
}
@font-face {
    font-family: 'ui-font-serif';
    font-weight: bold;
    font-style: normal;
    src: url('merriweather/Merriweather-Bold.ttf');
}
@font-face {
    font-family: 'ui-font-serif';
    font-weight: normal;
    font-style: italic;
    src: url('merriweather/Merriweather-Italic.ttf');
}
@font-face {
    font-family: 'ui-font-serif';
    font-weight: bold;
    font-style: italic;
    src: url('merriweather/Merriweather-BoldItalic.ttf');
}


* {
    box-sizing: border-box;
}

html {
    --bright-white: #FFF;
    --paper-white: #F5F5F5;

    --ink-black: #2A2A2A;
    --dark-grey: #4A4A4A;
    --medium-grey: #777;
    --light-grey: #E0E0E0;
    --bright-grey: #ECECEC;

    height: 100%;
    min-height: 100%;
    font-family: 'ui-font', sans-serif;
    font-weight: normal;
    font-size: 14px;
    line-height: 20px;
    background: var(--paper-white);
}
@media (prefers-color-scheme: dark) {
    html {
        --bright-white: #111;
        --paper-white: #1F1F1F;

        --ink-black: #CCC;
        --dark-grey: #DDD;
        --medium-grey: #808080;
        --light-grey: #333;
        --bright-grey: #2A2A2A;
    }
}

body {
    display: flex;
    height: 100%;
    min-height: 100%;
    background: var(--paper-white);
    color: var(--ink-black);
}
h1 {
    margin: 0;
    padding: .25em 0 0 0;
    font-weight: bold;
    font-size: 14px;
}
h2 {
    margin: 0;
    padding: 0;
    font-weight: normal;
    font-size: 14px;
}
p {
    padding: 0;
}
a,
a:link,
a:visited,
a:hover,
a:active {
    text-decoration: none;
    color: inherit;
}
.screenreader-only {
    width: 1px;
    height: 1px;
    position: absolute;
    top: -999px;
    left: -999px;
    overflow:hidden
}
.no-smooth {
    -webkit-font-smoothing: none;
}
.octicon {
    fill: currentColor;
}
.main-wrapper {
    width: 28em;
    height: 100%;
    max-height: 100%;
    float: left;
    position: relative;
    z-index: 2;
    border-right: 1px solid var(--medium-grey);
    overflow: hidden;
}
@media (prefers-color-scheme: dark) {
    .main-wrapper {
        border-right-color: var(--paper-white);
    }
}
.codemirror-wrapper {
    flex: 1;
    position: relative;
}
.info-wrapper {
    position: absolute;
    bottom: 1rem;
    right: 2rem;
    left: 2rem;
    z-index: 1;
    padding: 1em 1.4em;
    font-size: 10px;
    border: 1px solid var(--light-grey);
    border-radius: 3px;
    background: var(--bright-grey);
    color: var(--dark-grey);
}
@media screen and (max-width: 56em) {
    .info-wrapper {
        bottom: 2rem;
    }
}
@media screen and (max-height: 24em)  {
    .info-wrapper {
        display: none;
    }
}
.info-wrapper a:hover {
    color: var(--ink-black);
    text-decoration: underline;
}
.info-wrapper p {
    line-height: 14px;
}
.info-wrapper p:empty {
    display: none;
}
.info-wrapper p.variants::before {
    content: 'Variants: ';
    font-style: italic;
}
.info-wrapper h2::after {
    content: attr(data-license);
    margin-left: 1ex;
    font-size: 8px;
    color: var(--medium-grey);
    text-transform: uppercase;
}
main {
    display: flex;
    flex-direction: column;
    width: 100%;
    height: 100%;
}
section {
    flex: 0;
    flex-shrink: 0;
}
section#filters,
section.config {
    display: grid;
    grid-column-gap: 4px;
    grid-row-gap: 4px;
    grid-row-gap: 4px;
    grid-template-columns: auto auto auto auto;
    padding: 8px;
    font-size: 10px;
    background: var(--bright-grey);
}

section#filters {
    align-items: stretch;
    grid-template-columns: auto auto auto auto auto;
    border-bottom: 1px solid var(--light-grey);
}
section#filters input,
section#filters select {
    width: 100%;
    margin: 0;
}
section#filters > div {
    display: flex;
}

section.config {
    border-top: 1px solid var(--light-grey);
}
section.config > * {
    text-align: center;
    padding: 0;
    white-space: nowrap;
}
section.select-list {
    flex: 1;
    overflow: auto;
    -webkit-overflow-scrolling: touch;
}
section.select-list:focus {
    outline: none;
}
section.select-list > * {
    overflow: hidden;
}
section.select-list [data-style='serif'] .name {
    font-family: 'ui-font-serif', serif;
}
section.select-list .entry {
    position: relative;
    padding: 8px 12px;
}
section.select-list .entry:hover::before {
    transition: background .2s ease-in-out, left .1s ease-in-out, right .1s ease-in-out;
}
section.select-list .entry:not(:first-child)::before {
    content: '';
    display: block;
    position: absolute;
    top: 0;
    left: 6px;
    right: 6px;
    border-top: 1px solid var(--light-grey);
    pointer-events: none;
    z-index: -1;
}
section.select-list .entry:hover::before,
section.select-list .entry.active::before {
    content: '';
    display: block;
    position: absolute;
    top: 0;
    bottom: 0;
    right: 4px;
    left: 4px;
    border: 1px solid var(--light-grey);
    border-radius: 3px;
    pointer-events: none;
    z-index: -1;
    background: var(--bright-white);
}
section.select-list .entry:hover + .entry:not(:first-child)::before,
section.select-list .entry.active + .entry:not(:first-child)::before {
    border-top: none;
}
section.select-list .entry > a {
    display: block;
    flex: 1;
}
section.select-list .entry.filtered-out {
    display: none;
}
section.select-list a[data-value] {
    display: block;
    width: 66.67%;
    position: relative;
    overflow: hidden;
}
section.select-list a:hover .name,
section.select-list a:active .name {
    text-decoration: underline;
}
section.select-list .entry.active .name {
    font-weight: bold;
}
section.select-list a.website {
    position: absolute;
    top: 6px;
    right: 32px;
    padding: 0 4px;
    font-size: 10px;
    color: var(--paper-white);
    transition: all .2s ease-in-out;
}
section.select-list a.website svg {
    position: relative;
    top: 2px;
}
section.select-list a.website span {
    display: inline-block;
    padding-right: 1ex;
}
section.select-list a.favoritelink {
    position: absolute;
    top: 8px;
    right: 8px;
    color: var(--paper-white);
    cursor: pointer;
}
section.select-list .entry:not(.pinned) a.favoritelink svg {
    fill: transparent;
    stroke: currentColor;
    stroke-width: 1;
}
section.select-list .entry:hover a.website,
section.select-list .entry:hover a.favoritelink {
    color: var(--bright-grey);
}
section.select-list .entry:hover a.favoritelink:hover,
section.select-list a.favoritelink:hover {
    color: var(--ink-black);
}
section.select-list .entry.active a.website,
section.select-list .entry.active a.favoritelink {
    color: var(--dark-grey);
}
section.select-list .entry a.website:hover,
section.select-list .entry.pinned a.favoritelink {
    color: var(--ink-black);
}
section.select-list .pinned {
    background: var(--paper-white);
}
section.select-list .details {
    display: block;
    font-size: 10px;
    text-overflow: ellipsis;
    white-space: nowrap;
    overflow: hidden;
    color: var(--ink-black);
}
footer {
    padding: 0 8px 4px 8px;
    font-size: 10px;
    text-align: center;
    line-height: 20px;
    background: var(--bright-grey);
    color: var(--medium-grey);
}
footer h1 + p {
    font-style: italic;
}
footer a:hover,
footer a:active {
    text-decoration: underline;
    color: var(--ink-black);
}
footer p a,
footer p a:link,
footer p a:visited {
    text-decoration: underline;
}

label {
    margin-right: .5ex;
    color: var(--medium-grey);
}
button {
    all: unset;

    display: flex;
    align-items: center;
    justify-content: center;
    width: 20px;
    height: 18px;
    position: relative;
    margin: 0 .5ex 0 0;

    border: 1px solid var(--light-grey);
    border-radius: 3px;
    background: linear-gradient(var(--bright-white), var(--light-grey));
    color: var(--medium-grey);
    cursor: pointer;
}
@media (prefers-color-scheme: dark) {
    button {
        background: linear-gradient(var(--light-grey), var(--bright-white));
    }
}
button:hover {
    color: var(--ink-black);
}
button.selected {
    background: var(--bright-white);
    color: var(--ink-black);
    box-shadow: none;
}
button path,
button rect {
    fill: currentColor;
}
button:not(.selected) > svg:not(:first-child),
button.selected > svg:not(.selected) {
    display: none;
}
select {
    all: unset;
    width: 9em;
    padding: 0 .5ex;
    border: 1px solid var(--light-grey);
    border-radius: 3px;
    background: var(--paper-white);
    white-space: nowrap;
    overflow: hidden;
    text-overflow: ellipsis;
    cursor: pointer;
}
select option {
    background: var(--light-white);
    color: var(--ink-black);
}
input[type='text'] {
    line-height: 12px;
}
input[type='text'],
input[type='number'] {
    all: unset;
    width: 4em;
    margin-left: .5ex;
    padding: 0 .5ex;
    border: 1px solid var(--light-grey);
    border-radius: 3px;
    background: var(--paper-white);
    text-align: left;
    transition: all .2s ease-in-out;
}
input[type='text']:hover,
input[type='text']:focus,
input[type='number']:hover,
input[type='number']:focus,
select:hover,
select:focus {
    border-color: var(--medium-grey);
    background: var(--paper-white);
}
pre,
textarea,
.CodeMirror {
    height: auto;
    font-family: 'Input Mono', monospace;
}
form {
    height: 100%;
    min-height: 100%;
    position: relative;
    z-index: 1;
}
.nav-button {
    all: unset;
    font-family: inherit;
    cursor: pointer;
}
label + .nav-button {
    margin-left: .5ex;
}
.CodeMirror {
    height: 100%;
    min-height: 100%;
    border: none;
    font-size: 100%;
    background: transparent;
    box-shadow: none !important;
    text-rendering: optimizeLegibility;
}
.CodeMirror .cm-atom {
    font-style: italic;
}
.CodeMirror .cm-comment {
    font-weight: bold;
}
.CodeMirror-gutters {
    background: transparent;
}
.CodeMirror-scroll {
    -webkit-overflow-scrolling: touch;
}

footer .logo,
footer .support {
    display: inline-block;
    position: absolute;
    bottom: 4px;
    left: 8px;
    opacity: .5;
}
footer .logo svg path {
    fill: currentColor;
}
footer .logo * + * {
    margin-left: 8px;
}
footer .support {
    right: 8px;
    left: auto;
}

@media screen and (max-width: 56em) {
    body {
        display: block;
    }
    .main-wrapper,
    form {
        width: 100%;
        height: 50vh;
        min-height: 50vh;
        float: none;
    }
}

/* global CodeMirror */

import { Cookies } from './modules/cookies.js'
import { Fontsize } from './modules/fontsize.js'
import { Filters } from './modules/filters.js'
import { Language } from './modules/language.js'
import { Spacing } from './modules/spacing.js'
import { Theme } from './modules/theme.js'

let fontData

const fontsize = new Fontsize()

window.CMeditor = CodeMirror.fromTextArea(document.getElementById('code'), {
  lineNumbers: true,
  styleActiveLine: true,
  matchBrackets: true,
  theme: 'pastel-on-dark',
  lineWrapping: true
})

function isVisible (el) {
  const container = document.querySelector('section.select-list').getBoundingClientRect()
  const target = el.getBoundingClientRect()

  return target.bottom > container.top && target.top < container.bottom
}

/**
 * Get the font from the #, the cookie, or a default
 */
function getFont () {
  let font = window.location.hash.substring(1)

  if (!font) {
    font = Cookies.get('font')
  }

  if (!font) {
    font = 'monolisa'
  }

  return font
}

function setDetails (data) {
  const box = document.querySelector('.info-wrapper')
  box.querySelector('h2').setAttribute('data-license', data.license ?? '')
  box.querySelector('a').href = data.website
  box.querySelector('h2 a').textContent = data.name
  box.querySelector('p.info').textContent = data.description ?? ''
  if (typeof data.variants === 'string') {
    box.querySelector('p.variants').textContent = data.variants
  } else if (data.variants && data.variants.length > 1) {
    box.querySelector('p.variants').textContent = data.variants.join(', ')
  } else {
    box.querySelector('p.variants').textContent = ''
  }
}

// ProgrammingFonts font selector
function selectFont () {
  const codeMirror = document.querySelector('.CodeMirror')
  const font = getFont()

  if (typeof fontData !== 'undefined') {
    setDetails(fontData[font])
  }

  if (typeof fontData !== 'undefined' && fontData[font].rendering === 'bitmap') {
    codeMirror.classList.add('no-smooth')
    if (fontData[font]['bitmap size']) {
      fontsize.forceSize(fontData[font]['bitmap size'])
    }
  } else {
    codeMirror.classList.remove('no-smooth')
    fontsize.reset()
  }

  if (font === 'input') {
    codeMirror.style.fontFamily = 'Input Mono, monospace'
    codeMirror.querySelectorAll('pre, textarea').forEach((element) => {
      element.style.fontFamily = 'Input Mono, monospace'
    })
  } else {
    codeMirror.style.fontFamily = `${font}, monospace`
    codeMirror.querySelectorAll('pre, textarea').forEach((element) => {
      element.style.fontFamily = `${font}, monospace`
    })
  }

  document.querySelectorAll('#select-font [data-alias]').forEach((element) => {
    element.classList.remove('active')
  })

  const activeEntry = document.querySelector(`#select-font [data-alias='${font}']`)
  if (activeEntry) {
    activeEntry.classList.add('active')
    if (!isVisible(activeEntry)) {
      activeEntry.scrollIntoView({
        block: 'center',
        inline: 'nearest',
        behavior: 'smooth'
      })
    }
  }

  Cookies.set('font', font)
}

function renderSelectList () {
  const icon =
        '<svg class="octicon" viewBox="0 0 12 14" version="1.1" width="12" height="14" aria-hidden="true"><path fill-rule="evenodd" d="M11 10h1v3c0 .55-.45 1-1 1H1c-.55 0-1-.45-1-1V3c0-.55.45-1 1-1h3v1H1v10h10v-3zM6 2l2.25 2.25L5 7.5 6.5 9l3.25-3.25L12 8V2H6z"></path></svg>'
  const pinIcon =
        '<svg class="octicon octicon-pin" viewBox="0 0 16 16" version="1.1" width="16" height="16" aria-hidden="true"><path fill-rule="evenodd" d="M10 1.2V2l.5 1L6 6H2.2c-.44 0-.67.53-.34.86L5 10l-4 5 5-4 3.14 3.14a.5.5 0 0 0 .86-.34V10l3-4.5 1 .5h.8c.44 0 .67-.53.34-.86L10.86.86a.5.5 0 0 0-.86.34z"></path></svg>'
  let favoritesMap = {}
  let favorites = []

  document.getElementById('select-font').innerHTML = ''

  try {
    favorites = JSON.parse(localStorage.getItem('favorites')) || []
    favoritesMap = favorites.reduce((acc, alias) => {
      acc[alias] = true
      return acc
    }, {})
  } catch (err) {
    // eslint-disable-next-line no-console
    console.error('could not render favorites', err)
  }

  const renderAuthors = (authors) => {
    authors.sort()
    authors.forEach((author) => {
      const option = document.createElement('option')
      option.innerHTML = author
      document.getElementById('authors-list').querySelector('.other').appendChild(option)
    })
  }

  const renderFonts = (fonts) => {
    fonts.sort((a, b) => {
      if (favoritesMap[a.alias] && !favoritesMap[b.alias]) {
        return -1
      }
      if (!favoritesMap[a.alias] && favoritesMap[b.alias]) {
        return 1
      }
      if (a.name.toLowerCase() < b.name.toLowerCase()) {
        return -1
      }
      if (a.name.toLowerCase() > b.name.toLowerCase()) {
        return 1
      }
      return 0
    })

    fonts.forEach((v) => {
      const option = document.createElement('div')

      option.classList.add('entry')
      if (favoritesMap[v.alias]) {
        option.classList.add('pinned')
      }

      option.setAttribute('data-alias', v.alias)
      option.innerHTML = `<a href="#${v.alias}" data-style="${v.style}"><span class="name">${v.name}</span><span class="details">${v.year} — ${v.author}</span></a><a class="favoritelink" onclick="toggleFavorite('${v.alias}')">${pinIcon}</a><a class="website" href="${v.website}" rel="external"> <span>Website</span>${icon}</a>`

      document.getElementById('select-font').appendChild(option)
    })
  }

  fetch('fonts.json')
    .then((response) => {
      if (response.ok) {
        return response.json()
      } else {
        console.error(response.status + ': ' + response.statusText)
      }
    }).then((data) => {
      const fonts = []
      const authors = []

      fontData = data

      Object.keys(data).forEach((key) => {
        const v = data[key]
        v.alias = key
        fonts.push(v)
        if (authors.indexOf(v.author) < 0) {
          authors.push(v.author)
        }
      })

      renderAuthors(authors)
      renderFonts(fonts)
      selectFont()
      new Filters(data).init()
    })
}

window.toggleFavorite = (alias) => {
  try {
    let favorites = JSON.parse(localStorage.getItem('favorites')) || []
    if (favorites.indexOf(alias) > -1) {
      favorites = favorites.filter((v) => {
        return v !== alias
      })
    } else {
      favorites.push(alias)
    }
    localStorage.setItem('favorites', JSON.stringify(Array.from(new Set(favorites))))
  } catch (err) {
    // eslint-disable-next-line no-console
    console.error('could not save favorite', err)
  }
  renderSelectList()
  return false
}

function walk (direction) {
  const activeEntry = document.querySelector('.entry.active')
  let target = null
  let next = direction === 'up' ? activeEntry.previousElementSibling : activeEntry.nextElementSibling

  while (target === null) {
    if (next) {
      if (next.matches('.entry:not(.filtered-out)')) {
        target = next
      } else {
        next = direction === 'up' ? next.previousElementSibling : next.nextElementSibling
      }
    } else {
      target = false
    }
  }

  if (target) {
    target.querySelector('a').click()
  }
}

window.onhashchange = () => {
  selectFont()
}

window.addEventListener('DOMContentLoaded', () => {
  renderSelectList()
  fontsize.init()
  new Theme().init()
  new Spacing().init()
  new Language().init()

  document.querySelector('.select-list').onkeydown = (event) => {
    if (event.ctrlKey || event.altKey || event.metaKey || event.shiftKey) {
      return
    }
    if (event.key === 'ArrowUp') {
      event.preventDefault()
      event.stopPropagation()
      walk('up')
    } else if (event.key === 'ArrowDown') {
      event.preventDefault()
      event.stopPropagation()
      walk('down')
    }
  }

  document.body.addEventListener('keydown', (event) => {
    if (event.ctrlKey || event.metaKey) {
      if (event.key === '-') {
        event.preventDefault()
        event.stopPropagation()
        fontsize.down()
      } else if (event.key === '=') {
        event.preventDefault()
        event.stopPropagation()
        fontsize.up()
      }
    }
  })
})

