'use strict';

var gulp = require('gulp');
var sass = require('gulp-sass');
var rename = require('gulp-rename');

gulp.task('sass', function () {
  return gulp.src('./src/pills.scss')
    .pipe(sass.sync({outputStyle: 'expanded'}).on('error', sass.logError))
    .pipe(gulp.dest('./dist'))
    .pipe(rename({suffix: '.min'}))
    .pipe(sass.sync({outputStyle: 'compressed'}).on('error', sass.logError))
    .pipe(gulp.dest('./dist'))
});

gulp.task('watch', ['sass'], function () {
  gulp.watch('./src/*.scss', ['sass']);
});

gulp.task('default', ['watch']);

{
  "name": "pills",
  "version": "1.0.1",
  "description": "A simple, responsive, and tiny CSS grid for humans.",
  "main": "dist/pills.min.css",
  "repository": {
    "type": "git",
    "url": "git+https://github.com/rohitkrai03/pills.git"
  },
  "keywords": [
    "css",
    "grid",
    "responsive",
    "framework"
  ],
  "author": "Rohit Rai <rohitkrai03@gmail.com> (http://arkpod.in/)",
  "license": "MIT",
  "bugs": {
    "url": "https://github.com/rohitkrai03/pills/issues"
  },
  "homepage": "https://github.com/rohitkrai03/pills#readme",
  "devDependencies": {
    "gulp": "^3.9.1",
    "gulp-rename": "^1.2.2",
    "gulp-sass": "^2.3.1"
  }
}
