#root.root {
  --tabs-height: 33px;
  --tabs-pinned-height: 44px;
  --tabs-margin: 8px;
  --tabs-indent: 18px;
  --tabs-close-btn-margin: 5px;
  --tabs-inner-gap: 10px;
  --general-border-radius: 6px;
  --tabs-color-layer-opacity: 0.25;
  --tabs-activated-bg: rgba(255, 255, 255, 0.12);
}
#root .popup-container {
  background-color: transparent;
}
#root .hidden-panels-popup-layer:before {
  background-color: transparent;
}

/* PINNED TABS */

.Tab[data-pin="true"] .body {
  box-shadow: 0px 2px 1px #00000000;
  background: #f2f2f220;
  color: #ffffff60;
}

.Tab[data-pin="true"][data-active="true"] {
  background-color: #ffffff40;
  border-radius: calc(var(--general-border-radius) + 2px);
}

.Tab[data-discarded="true"] > .body > .fav {
  opacity: 1;
}

#root .TabsPanel .PinnedTabsBar {
  display: flex;
  flex-wrap: wrap;
  margin: 0;
  padding: 0;
}

.PinnedTabsBar .tab-wrapper {
  flex-grow: 1;
  min-width: 55px;
  --tabs-pinned-width: auto;
}

/* GENERAL */

.AnimatedTabList .Tab {
  width: 100%;
}

.AnimatedTabList .Tab .title,
.TabsPanel .AnimatedTabList .new-tab-btn:after {
  font-size: 0.85rem;
}

body {
  height: 100vh;
  overflow: hidden;
}

.AnimatedTabList .bottom-space {
  display: none;
}

/* SEPARATOR */

#root .top-shadow,
#root .bottom-shadow {
  border: #f2f2f220 1px solid;
  box-shadow: none;
  height: 1px;
  border-radius: var(--general-border-radius);
}

#root .top-shadow {
  top: 0;
}

#root .bottom-shadow {
  bottom: 0;
}

.Tab .close-icon {
  height: 24px;
  width: 24px;
  top: calc(50% - 12px);
  left: calc(50% - 12px);
}

/* Smoothen masking for overflowed tab title. */
.Tab .title {
  background: linear-gradient(90deg, var(--tabs-normal-fg) 80%, #f1f1f100 99%);
  background-clip: text;
  color: transparent;
}

.TabsPanel .Tab {
  margin: 0;
}

.TabsPanel {
  display: flex;
  gap: 12px;
}

/* Uncomment this to apply Segoe UI Variable font - Make sure to install the font first */
#root.root {
  --tabs-font: 0.9375rem Segoe UI Variable Small, Segoe UI;
}

/* NEW TAB BUTTON*/
.TabsPanel .new-tab-btns {
  position: relative;
}

.TabsPanel .new-tab-btn {
  margin: calc(var(--tabs-margin) * 0.5) 0;
  --ntb-btn-height: 37px;
}

.TabsPanel .new-tab-btn:first-of-type {
  justify-content: left;
  padding-left: calc(var(--tabs-inner-gap));
  margin: calc(var(--tabs-margin) * 0.5) 0;
  --ntb-btn-height: 37px;
}

.TabsPanel .new-tab-btn:first-of-type >svg,
.TabsPanel .new-tab-btn:first-of-type >img {
  fill: transparent;
  background-color: var(--nav-btn-fg);
  opacity: 40%;
  width: 17px;
  height: 17px;
  scale: 0.9;
  mask: url("data:image/svg+xml;base64,");
}

.TabsPanel .new-tab-btn:first-of-type > svg.-badge,
.TabsPanel .new-tab-btn:first-of-type > img.-badge {
	width: 0;
	height: 0;
}

.TabsPanel .new-tab-btn:first-of-type:after {
  justify-content: left;
  content: "New Tab";
  font: var(--tabs-font);
  padding-left: calc(var(--tabs-inner-gap) + var(--tabs-margin) + 12px);
  color: var(--nav-btn-fg);
  opacity: 40%;
}

/* NAVBAR */

#root.root {
  --nav-btn-width: 25px;
}
#root.root {
  --nav-btn-height: 25px;
}
#root.root {
  --nav-btn-margin: 2px;
}
#root.root {
  --toolbar-bg: transparent;
}

/* Moving Sidebery navigation bar to bottom, to mimic how Space works on Arc Browser. Make sure to choose Horizontal when activating the navigation bar. */
.NavigationBar {
  box-shadow: none;
}
.top-horizontal-box {
  display: flex;
  order: 1;
  margin-left: 5px;
  margin-right: 5px;
}

/* Remove background color, border and shadow for active tab panel. */
.NavigationBar .nav-item[data-active="true"] {
  background-color: transparent;
  box-shadow: none;
}

/* Making non-active tabs (including bookmark panels) grayscale. */
.NavigationBar .nav-item[data-type="tabs"][data-active="false"] .icon {
  fill: var(--nav-btn-fg);
  opacity: 40%;
  scale: 0.75;
}
.NavigationBar .nav-item[data-type="bookmarks"][data-active="false"] .icon {
  fill: var(--nav-btn-fg);
  opacity: 40%;
  scale: 0.75;
}

/* Settings related to Hidden Panels icon and popup layer, adapting to the flipped navbar orientation. */
#hidden_panels_btn.nav-item .icon {
  transform: scaleY(-1);
  opacity: 40%;
  scale: 0.9;
}
.hidden-panels-popup-layer {
  transform: scaleY(-1);
  margin-top: -15%;
  --toolbar-bg: var(--frame-bg);
}
.NavigationBar .hidden-panels-popup-content {
  transform: scaleY(-1);
}

/* Adjust 'add tabs panel' size & position. */
#root .PanelConfigPopup .popup {
  width: 80vw;
  margin-top: 25vh;
}

/* Replacing 'add tabs panel' button with Fluent plus icon to mimic Arc's 'new space' button. */
.NavigationBar #add_tp.nav-item .icon {
  fill: transparent;
  background-color: var(--nav-btn-fg);
  width: 80%;
  height: 80%;
  mask: url("data:image/svg+xml;base64,");
}

/* Replacing 'history' panel with Fluent archive icon to mimic Arc's 'recently closed' button. */
.NavigationBar #navhistory.nav-item .icon {
  fill: transparent;
  background-color: var(--nav-btn-fg);
  display: flex;
  padding: auto;
  width: 80%;
  height: 80%;
  mask: url("data:image/svg+xml;base64,");
}

/* Replacing Sidebery 'settings' with Fluent settings icon to blend with Arc (Windows) aesthetic. */
.NavigationBar #settings.nav-item .icon {
  fill: transparent;
  background-color: var(--nav-btn-fg);
  width: 75%;
  height: 75%;
  mask: url("data:image/svg+xml;base64,");
}

/* ArcWTF main files */
@import url("icons/icons.css");
@import url("toolbar/tabbar.css");
@import url("toolbar/navbar.css");
@import url("toolbar/personalbar.css");
@import url("toolbar/findbar.css");
@import url("toolbar/urlbar.css");
@import url("global/colors.css");
@import url("global/popup.css");
@import url("global/browser.css");
@import url("global/tree.css");

/* Tweaks */
@import url("global/tweaks.css");
@import url("tweaks/hide-tabs-bar.css");
@import url("tweaks/extensions.css");
@import url("tweaks/sidebar.css");
@import url("tweaks/popup-search.css");

/* Enable translucency on browser toolbars. (MacOS only) */
/**/@import url("tweaks/mac-translucent.css") (-moz-bool-pref: "uc.tweak.mac-translucent");/**/

/* Import custom stylesheet instead of modifying the theme files. */
@import url("custom.css");

/* Replacing Userchrome Toggle icon with Arc sidebar icon */
:is(.webextension-browser-action, .eom-addon-button)[data-extensionid="userchrome-toggle@joolee.nl"] .toolbarbutton-icon { list-style-image: url(./icons/userchrome-toggle.svg); }
:is(.webextension-browser-action, .eom-addon-button)[data-extensionid="copy-frame-url@jeffersonscher.com"] .toolbarbutton-icon { list-style-image: url(./icons/link.svg); }

