  "dependencies": {
    "alltomp3": "^0.4.5",
    "electron-updater": "~4.2.0",
    "ncp": "~2.0.0",
    "nedb-promise": "~2.0.1",
    "request": "~2.81.0"
  },
  "devDependencies": {
    "electron": "~4",
    "electron-builder": "~22.3.2",
    "unzip2": "~0.2.5"
  },
  "build": {
    "appId": "org.alltomp3.app",
    "asarUnpack": [
      "**/node_modules/sharp/**/*",
      "bin",
      "bin/**"
    ],
    "mac": {
      "category": "public.app-category.music"
    },
    "linux": {
      "target": "deb",
      "executableName": "alltomp3",
      "category": "AudioVideo",
      "packageCategory": "sound"
    },
    "deb": {
      "depends": [
        "python-pathlib",
        "gconf2",
        "gconf-service",
        "libnotify4",
        "libappindicator1",
        "libxtst6",
        "libnss3",
        "ffmpeg",
        "libchromaprint-tools"
      ]
    },
    "dmg": {
      "title": "AllToMP3",
      "icon": "build/dmg.icns",
      "contents": [
        {
          "x": 0,
          "y": 90
        },
        {
          "x": 220,
          "y": 90,
          "type": "link",
          "path": "/Applications"
        }
      ],
      "iconSize": 100,
      "window": {
        "width": 400,
        "height": 180
      }
    },
    "directories": {
      "app": "."
    },
    "files": [
      "!**/app/node_modules",
      "!**/app/src"
    ],
    "publish": [
      {
        "provider": "github"
      }
    ]
  },
  "scripts": {
    "start": "ALLTOMP3_DEV=1 electron .",
    "pack": "electron-builder --dir",
    "dist": "electron-builder",
    "release": "electron-builder",
    "postinstall": "electron-builder install-app-deps && node ./install.js"
  }
}
