	"version": "0.3",
	"launch_path": "/",
	"icons": {
		"30": "/icons/icon-30.png",
		"60": "/icons/icon-60.png",
		"128": "/icons/icon-128.png"
	},
	"developer": {
		"name": "Lim Chee Aun",
		"url": "http://cheeaun.com/"
	}
}

