{
	"root": true,
	"env": {
		"browser": true,
		"node": true,
		"es2020": true
	},
	"parserOptions": {
		"sourceType": "module"
	},
	"extends": "eslint:recommended",
	"rules": {
		"brace-style": ["warn", "stroustrup", { "allowSingleLine": true }],
		"func-call-spacing": ["warn", "never"],
		"indent": ["warn", "tab", { "ignoredNodes": ["TemplateLiteral *"], "SwitchCase": 1 }],
		"linebreak-style": ["warn", "unix"],
		"no-console": ["warn", { "allow": ["warn", "error"] }],
		"quotes": ["warn", "double"],
		"semi": ["warn", "always"],
		"space-before-blocks": ["warn", "always"]
	},
	"overrides": [
		{
			"files": ["*.json"],
			"parser": "jsonc-eslint-parser",
			"extends": ["plugin:jsonc/recommended-with-jsonc"],
			"rules": {
				"jsonc/sort-keys": [
					"warn",
					{
						"pathPattern": ".*", // Hits the all properties
						"hasProperties": ["type"],
						"order": [
							"type",
							"properties",
							"items",
							"required",
							"minItems",
							"additionalProperties",
							"additionalItems"
						]
					},
					{
						"pathPattern": ".*",
						"order": { "type": "asc" }
					}
				]
			}
		},
		{
			"files": ["metadata.json", "schemas/*.json"],
			"parser": "jsonc-eslint-parser",
			"extends": ["plugin:jsonc/recommended-with-jsonc"],
			"rules": {
				"jsonc/sort-keys": [0]
			}
		},
		{
			"files": ["project.json"],
			"rules": {
				"jsonc/sort-keys": [
					"warn",
					{
						"pathPattern": "^$",
						"order": [
							"$schema",
							"name",
							"tags",
							"implicitDependencies",
							"targets"
						]
					},
					{
						"pathPattern": ".*",
						"order": { "type": "asc" }
					}
				]
			}
		},
		{
			"files": ["package.json"],
			"rules": {
				"jsonc/sort-keys": [
					"warn",
					{
						"pathPattern": "^$",
						"order": [
							"$schema",
							"private",
							"name",
							"version",
							"description",
							"license",
							"author",
							"maintainers",
							"contributors",
							"homepage",
							"repository",
							"bugs",
							"type",
							"exports",
							"main",
							"module",
							"browser",
							"man",
							"preferGlobal",
							"bin",
							"files",
							"directories",
							"scripts",
							"config",
							"sideEffects",
							"types",
							"typings",
							"workspaces",
							"resolutions",
							"dependencies",
							"bundleDependencies",
							"bundledDependencies",
							"peerDependencies",
							"peerDependenciesMeta",
							"optionalDependencies",
							"devDependencies",
							"keywords",
							"engines",
							"engineStrict",
							"os",
							"cpu",
							"publishConfig"
						]
					},
					{
						"pathPattern": "^repository$",
						"order": ["type", "url", "directory"]
					},
					{
						"pathPattern": ".*",
						"order": { "type": "asc" }
					}
				]
			}
		},
		{
			"files": [
				"components/*/stories/*.js",
				".storybook/*.js",
				".storybook/**/*.js"
			],
			"parserOptions": {
				"ecmaVersion": "latest",
				"sourceType": "module",
				"ecmaFeatures": {
					"impliedStrict": true
				}
			}
		}
	]
}

{
	"tabWidth": 2,
	"useTabs": true,
	"overrides": [
    {
      "files": "*.css",
      "options": {
        "printWidth": 500
      }
    }
  ]
}

module.exports = {
	extends: ["@commitlint/config-conventional"],
};

module.exports = {
	"*.css": [
		"stylelint --fix --cache --allow-empty-input --report-descriptionless-disables --report-invalid-scope-disables --report-needless-disables",
		"prettier --no-config --no-error-on-unmatched-pattern --ignore-unknown --log-level silent --write --config .prettierrc",
	],
	"*.{js,json}": [
		"eslint --fix --cache --no-error-on-unmatched-pattern --quiet --ignore-pattern \"!.storybook/\""
	],
	"dist/*.css": [
		"prettier --no-config --no-error-on-unmatched-pattern --ignore-unknown --log-level silent --write --config .prettierrc"
	],
	"components/*/metadata/metadata.json": (files) => {
		return [
			...(files.map(file => `pajv test --valid -s ./schemas/metadata.schema.json -d "${file}"`) ?? []),
			`prettier --no-config --no-error-on-unmatched-pattern --ignore-unknown --log-level silent --write ${files.join(" ")} --config .prettierrc`
		];
	},
	"*.{md,mdx}": [
		"prettier --no-config --no-error-on-unmatched-pattern --ignore-unknown --log-level silent --write --config .prettierrc"
	]
};

{
	"$schema": "./node_modules/nx/schemas/nx-schema.json",
	"cli": {
		"analytics": false,
		"packageManager": "yarn",
		"warnings": {
			"versionMismatch": true
		}
	},
	"namedInputs": {
		"core": ["{projectRoot}/*.css", "{projectRoot}/themes/*.css"],
		"eslint": ["{workspaceRoot}/.eslintrc"],
		"metadata": ["{projectRoot}/metadata/*.json"],
		"prettier": ["{workspaceRoot}/.prettierrc"],
		"scripts": ["{projectRoot}/stories/*.js"],
		"stylelint": [
			"{workspaceRoot}/.stylelintignore",
			"{workspaceRoot}/stylelint.config.js",
			"{workspaceRoot}/plugins/stylelint-*/index.js"
		],
		"tools": [
			"{projectRoot}/*.json",
			"{workspaceRoot}/postcss.config.mjs",
			"{workspaceRoot}/plugins/postcss-*/index.js"
		]
	},
	"pluginsConfig": {
		"@nx/js": {
			"analyzeSourceFiles": false
		}
	},
	"targetDefaults": {
		"build": {
			"cache": true,
			"dependsOn": [
				"^build",
				"clean"
			],
			"executor": "nx:run-commands",
			"inputs": [
				"core",
				"tools",
				"{workspaceRoot}/tasks/component-builder.js",
				"!{projectRoot}/stories/*.js",
				{ "externalDependencies": ["postcss"] },
				{ "env": "NODE_ENV" }
			],
			"options": {
				"commands": [
					"node --no-warnings -e 'require(\"./tasks/component-builder.js\").default()'"
				]
			},
			"outputs": [
				"{projectRoot}/dist/*.{css,json}",
				"{projectRoot}/dist/themes/*.css",
				"{projectRoot}/dist/*.map",
				"{projectRoot}/dist/themes/*.css.map",
				"{projectRoot}/metadata/mods.md"
			]
		},
		"clean": {
			"cache": true,
			"executor": "nx:run-commands",
			"inputs": [
				"{projectRoot}/dist/*.{css,json}",
				"{projectRoot}/dist/**/*.{css,json}",
				"{projectRoot}/dist/*.map",
				"{projectRoot}/dist/**/*.map",
				{ "externalDependencies": ["rimraf"] }
			],
			"options": {
				"commands": [
					"rimraf {projectRoot}/dist",
					"test -d {projectRoot}/dist && echo \"Error: dist directory could not be removed\" && exit 1 || exit 0"
				],
				"parallel": false
			},
			"outputs": []
		},
		"compare": {
			"cache": true,
			"dependsOn": ["build"],
			"executor": "nx:run-commands",
			"inputs": [
				"{workspaceRoot}/tasks/compare-compiled-output.js",
				{ "dependentTasksOutputFiles": "dist/*.css", "transitive": true },
				{
					"externalDependencies": [
						"fast-glob",
						"tar",
						"lodash",
						"nunjucks",
						"rimraf",
						"npm-registry-fetch",
						"yargs",
						"diff",
						"diff2html",
						"colors"
					]
				},
				{ "env": "NODE_ENV" }
			],
			"options": {
				"commands": [
					"node --no-warnings ./tasks/compare-compiled-output.js $NX_TASK_TARGET_PROJECT"
				]
			},
			"outputs": ["{workspaceRoot}/.diff-output"]
		},
		"format": {
			"cache": true,
			"executor": "nx:run-commands",
			"inputs": [
				"core",
				"scripts",
				"stylelint",
				"eslint",
				"prettier",
				{ "externalDependencies": ["stylelint", "eslint", "prettier"] }
			],
			"options": {
				"commands": [
					"prettier --write --cache --log-level error --ignore-unknown --no-error-on-unmatched-pattern {projectRoot}/*.css {projectRoot}/**/*.css && stylelint --fix --cache --allow-empty-input {projectRoot}/*.css {projectRoot}/**/*.css",
					"eslint --fix --cache --no-error-on-unmatched-pattern {projectRoot}/*.json {projectRoot}/stories/*.js",
					"prettier --write --cache --log-level error --ignore-unknown --no-error-on-unmatched-pattern {projectRoot}/*.{md,mdx} {projectRoot}/**/*.{md,mdx}"
				]
			}
		},
		"lint": {
			"cache": true,
			"executor": "nx:run-commands",
			"inputs": [
				"core",
				"scripts",
				"stylelint",
				"eslint",
				"prettier",
				{ "externalDependencies": ["stylelint", "eslint", "prettier"] }
			],
			"options": {
				"commands": [
					"stylelint --cache --allow-empty-input --report-descriptionless-disables --report-invalid-scope-disables --report-needless-disables {projectRoot}/*.css {projectRoot}/**/*.css --ignore-pattern {projectRoot}/dist",
					"eslint --cache --no-error-on-unmatched-pattern --report-unused-disable-directives {projectRoot}/*.{js,json} {projectRoot}/**/*.{js,json} --ignore-pattern {projectRoot}/dist || exit 0"
				]
			}
		},
		"report": {
			"cache": true,
			"executor": "nx:run-commands",
			"inputs": [
				"core",
				"tools",
				"{workspaceRoot}/tasks/component-report.js",
				{ "env": "NODE_ENV" }
			],
			"options": {
				"commands": [
					"cross-env NODE_OPTIONS=\"--no-warnings\" node -e 'require(\"./tasks/component-reporter.js\").default()'"
				]
			},
			"outputs": [
				"{projectRoot}/metadata/metadata.json"
			]
		},
		"test": {
			"cache": true,
			"configurations": {
				"plugins": {
					"commands": ["ava test.js"],
					"cwd": "{projectRoot}",
					"inputs": [
						"{projectRoot}/index.js",
						"{projectRoot}/test.js",
						"{projectRoot}/expected/*.css",
						"{projectRoot}/fixtures/*.css"
					]
				},
				"scope": {
					"commands": [
						"chromatic --build-script-name build --junit-report --only-story-names --skip '@(changeset-release/**|dependabot/**)'"
					]
				}
			},
			"executor": "nx:run-commands",
			"inputs": ["scripts", "tools", { "externalDependencies": ["chromatic"] }],
			"options": {
				"commands": [
					"chromatic --only-changed --build-script-name build --junit-report"
				],
				"cwd": ".storybook"
			}
		},
		"validate": {
			"executor": "nx:run-commands",
			"inputs": [
				"{workspaceRoot}/schemas/metadata.schema.json",
				"metadata",
				{ "externalDependencies": ["pajv"] }
			],
			"options": {
				"commands": [
					"test -e {projectRoot}/metadata && pajv validate -s ./schemas/metadata.schema.json -d \"{projectRoot}/metadata/metadata.json\" || exit 0"
				]
			}
		}
	}
}

