const { join, sep, basename } = require("path");

module.exports = ({
	file,
	to,
	splitinatorOptions = {
		noSelectors: false,
		noFlatVariables: false,
		// @todo strip out all but the references to --system- variables
		// NOT --system- definitions, only references
		referencesOnly: false,
	},
	resolveImports = true,
	shouldCombine = false,
	lint = true,
	verbose = true,
	additionalPlugins = {},
	env = process.env.NODE_ENV ?? "development",
	...options
} = {}) => {
	const isProduction = env.toLowerCase() === "production";

	if (typeof options?.map === "undefined") {
		options.map = isProduction ? false : { inline: false };
	}

	const rootPath = __dirname;
	const outputFilepath = to ?? file;
	const relativePath = outputFilepath?.replace(rootPath, "");

	const outputFilename = outputFilepath ? basename(outputFilepath, ".css") : undefined;
	const pathParts = relativePath?.split(sep) ?? [];

	if (["themes", "spectrum", "express"].some(foldername => pathParts.includes(foldername)) || outputFilename === "index-theme") {
		splitinatorOptions.noSelectors = true;
	}

	if (outputFilename === "express" || pathParts.includes("express")) shouldCombine = true;

	if (outputFilename === "index-base") {
		splitinatorOptions.noFlatVariables = true;
	}

	if (pathParts.includes("bridge")) {
		splitinatorOptions.referencesOnly = true;
	}

	return {
		...options,
		plugins: {
			/* --------------------------------------------------- */
			/* ------------------- IMPORTS ---------------- */
			/** @link https://github.com/postcss/postcss-import#postcss-import */
			"postcss-import": resolveImports ? {} : false,
			/* --------------------------------------------------- */
			/* ------------------- SASS-LIKE UTILITIES ----------- */
			"postcss-extend": {},
			"postcss-hover-media-feature": {},
			"postcss-pseudo-classes": !isProduction ? {
				restrictTo: ["focus-visible", "focus-within", "hover", "active", "disabled"],
				allCombinations: true,
				preserveBeforeAfter: false,
				prefix: "is-"
			} : false,
			/* --------------------------------------------------- */
			/* ------------------- VARIABLE PARSING -------------- */
			"postcss-splitinator": {
				processIdentifier: (identifier) =>
					identifier === "express" ? "spectrum--express" : identifier,
				...splitinatorOptions,
			},
			"postcss-combininator": shouldCombine ? {
				newSelector: ".spectrum",
			} : false,
			...additionalPlugins,
			/* --------------------------------------------------- */
			/* ------------------- POLYFILLS --------------------- */
			/**
			 * @todo should we be documenting this for downstream users rather
			 * than polyfilling the features ourselves? what if they want to
			 * use a different support matrix?
			 *
			 * @note stage 2 (default); stage 4 === stable
			 * @link https://github.com/csstools/postcss-plugins
			 * @link https://preset-env.cssdb.org/features/#stage-2
			 */
			"postcss-preset-env": {
				stage: 2,
				env,
				features: {
					"custom-properties": true,
					"logical-properties-and-values": false,
					clamp: true,
					"color-functional-notation": true,
					"dir-pseudo-class": { preserve: true },
					"nesting-rules": { noIsPseudoSelector: true },
					// https://github.com/jsxtools/focus-within
					"focus-within-pseudo-class": true,
					"font-format-keywords": true,
					"opacity-percentage": true,
					// https://github.com/csstools/postcss-plugins/tree/main/plugins/css-prefers-color-scheme
					"prefers-color-scheme-query": true,
				},
			},
			/* --------------------------------------------------- */
			/* ------------------- ORGANIZE/DEDUPE --------------- */
			"at-rule-packer": {},
			cssnano: {
				preset: [
					"cssnano-preset-advanced",
					{
						colormin: false,
						reduceIdents: false,
						discardUnused: false,
						discardComments: {
							removeAll: true
						},
						// @todo yarn add -DW css-declaration-sorter
						cssDeclarationSorter: false, // @todo { order: "smacss" }
						normalizeWhitespace: isProduction,
					},
				],
			},
			/* --------------------------------------------------- */
			/* ------------------- REPORTING --------------------- */
			stylelint: {
				cache: true,
				// Passing the config path saves a little time b/c it doesn't have to find it
				configFile: join(__dirname, "stylelint.config.js"),
				quiet: !lint,
				fix: true,
				allowEmptyInput: true,
				ignorePath: join(__dirname, ".stylelintignore"),
				reportNeedlessDisables: lint,
				reportInvalidScopeDisables: lint,
			},
			"postcss-licensing": {
				filename: "COPYRIGHT",
				cwd: __dirname,
				skipIfEmpty: true,
			},
			"postcss-reporter": verbose
				? {
					clearAllMessages: true,
					noIcon: true,
				}
				: false,
		},
	};
};

module.exports = {
	allowEmptyInput: true,
	cache: true,
	defaultSeverity: "warning",
	extends: ["stylelint-config-standard"],
	plugins: [
		"stylelint-header",
		"stylelint-selector-bem-pattern",
		"stylelint-order",
		"stylelint-use-logical",
		"@spectrum-tools/stylelint-no-missing-var",
		"@spectrum-tools/stylelint-no-unused-custom-properties",
		"@spectrum-tools/stylelint-no-unknown-custom-properties",
		"@spectrum-tools/theme-alignment",
		"stylelint-high-performance-animation",
	],
	rules: {
		/** --------------------------------------------------------------
		 * Disabled rules
		 * -------------------------------------------------------------- */
		"custom-property-empty-line-before": null,
		"declaration-block-no-redundant-longhand-properties": null,
		"declaration-empty-line-before": null,
		"import-notation": null,
		"no-descending-specificity": null,
		"no-duplicate-selectors": null,

		/** --------------------------------------------------------------
		 * Customized rule settings
		 * -------------------------------------------------------------- */
		/** @note use floats for opacity because it minifies better than percent */
		"alpha-value-notation": ["percentage", { exceptProperties: ["opacity"] }],
		"at-rule-empty-line-before": [
			"always",
			{
				except: ["blockless-after-blockless", "first-nested"],
				ignore: ["after-comment", "first-nested"],
				ignoreAtRules: ["extend"],
			},
		],
		"at-rule-no-unknown": [
			true,
			{
				ignoreAtRules: ["extend", "each", "include", "mixin"],
			},
		],
		"block-no-empty": [true, {
			ignore: ["comments"],
		}],
		"color-function-notation": ["modern", { ignore: ["with-var-inside"] }],
		"comment-empty-line-before": [
			"always",
			{
				except: ["first-nested"],
				ignore: ["after-comment", "stylelint-commands"],
				// don't require a newline before a passthrough flag
				ignoreComments: [/^@?passthroughs?/],
			},
		],
		"custom-property-pattern": [/^(spectrum|mod|highcontrast|system|_)/, {}],
		"declaration-block-no-duplicate-custom-properties": true,
		"declaration-property-value-no-unknown": [
			true,
			{
				ignoreProperties: {
					color: ["CanvasText"],
					"forced-color-adjust": ["preserve-parent-color"],
				},
			},
		],
		"declaration-block-no-shorthand-property-overrides": true,
		"function-no-unknown": [
			true,
			{
				severity: "warning",
			},
		],
		"max-nesting-depth": [3, { severity: "warning" }],
		"property-no-unknown": [
			true,
			{
				checkPrefixed: true,
			},
		],
		"rule-empty-line-before": [
			"always",
			{
				except: ["first-nested"],
				ignore: ["after-comment"],
			},
		],
		"selector-attribute-quotes": "always",
		"selector-class-pattern": [
			"^(spectrum-|is-|u-)[A-Za-z0-9-]+", {
				resolveNestedSelectors: true
			}
		],
		"selector-not-notation": "complex",
		"value-keyword-case": [
			"lower",
			{
				camelCaseSvgKeywords: true,
				ignoreKeywords: ["Transparent", "Text"],
			},
		],
		"value-no-vendor-prefix": [
			true,
			{
				disableFix: true,
				severity: "warning",
			},
		],

		/** --------------------------------------------------------------
		 * Plugins
		 * -------------------------------------------------------------- */
		"csstools/use-logical": true,
		"header/header": [
			"./COPYRIGHT",
			{
				nonMatchingTolerance: 0.8,
			},
			{
				fix: true,
			},
		],
		"order/order": ["custom-properties", "declarations"],
		"plugin/selector-bem-pattern": [
			{
				preset: "suit",
				presetOptions: { namespace: "spectrum" },
				utilitySelectors: /^\.(is|u)-[A-z0-9]+$/,
				componentName: /^[A-Z][A-z0-9]+$/,
			},
			{
				severity: "warning",
			},
		],
		/** Performance */
		"plugin/no-low-performance-animation-properties": [
			true,
			{ severity: "warning" },
		],

		/** --------------------------------------------------------------
		 * Local/custom plugins
		 * -------------------------------------------------------------- */
		"spectrum-tools/no-missing-var": true,
		"spectrum-tools/no-unknown-custom-properties": [
			true,
			{
				/** @note this is a list of custom properties that are allowed to be unknown */
				ignoreList: [
					/^--mod-/,
					/^--highcontrast-/,
					/^--system-/,
					/^--spectrum-picked-color$/,
				],
				skipDependencies: false,
				disableFix: true,
				severity: "warning",
			},
		],
		/** @note this enables reporting of unused variables in a file */
		"spectrum-tools/no-unused-custom-properties": [
			true,
			{
				ignoreList: [/^--mod-/],
				disableFix: true,
				severity: "warning",
			},
		],
	},
	/** --------------------------------------------------------------
	 * Overrides
	 * -------------------------------------------------------------- */
	overrides: [
		{
			files: [".storybook/assets/*.css"],
			rules: {
				"custom-property-pattern": null,
				"color-function-notation": null,
				"spectrum-tools/no-unused-custom-properties": null,
				"spectrum-tools/no-unknown-custom-properties": null,
			},
		},
		{
			/* Validate that the legacy themes don't introduce any new selectors or custom properties */
			files: ["components/*/themes/spectrum.css", "components/*/themes/express.css", "tokens/**/*.css"],
			rules: {
				"spectrum-tools/no-unused-custom-properties": null,
				"spectrum-tools/no-unknown-custom-properties": null,
			}
		},
		{
			/* Validate that the legacy themes don't introduce any new selectors or custom properties */
			files: ["components/*/themes/express.css", "!components/*/themes/spectrum.css"],
