var Ractive = require('ractive')
var page = require('page')
var fs = require('fs')
var Client = require('electron-rpc/client')
var client = new Client()

Ractive.DEBUG = false

// Throw unhandled javascript errors
window.onerror = function errorHandler (message, url, lineNumber) {
  message = message + '\n' + url + ':' + lineNumber
  throwError(message)
}

var templates = {
  configure: fs.readFileSync(__dirname + '/configure.tmpl').toString(),
  detail: fs.readFileSync(__dirname + '/detail.tmpl').toString(),
  about: fs.readFileSync(__dirname + '/about.html').toString()
}

var state = {}

var events = {
  processAction: function (e) {
    var action = e.node.attributes['data-action'].value
    var procNameAttr = e.node.attributes['data-name']
    var data = {task: action}
    if (procNameAttr) data.name = procNameAttr.value
    execTask(data)
  },

  quit: function () {
    client.request('terminate')
  },

  openDir: function () {
    client.request('open-dir')
  },

  openKillMenu: function (e) {
    var remote = require('remote')
    var Menu = remote.require('menu')
    var MenuItem = remote.require('menu-item')

    function sendSignal (signal) {
      return function () {
        var procNameAttr = e.node.attributes['data-name']
        execTask({task: 'stop', name: procNameAttr.value, signal: signal})
      }
    }

    var menu = new Menu()
    ;['SIGTERM', 'SIGHUP', 'SIGINT', 'SIGKILL', 'SIGQUIT'].forEach(function (signal) {
      menu.append(new MenuItem({ label: 'Send ' + signal, click: sendSignal(signal) }))
    })

    menu.popup(remote.getCurrentWindow())
    return false
  },

  openLogsDir: function (e) {
    client.request('open-logs-dir', {name: e.context.name})
  }
}

var routes = {
  configure: function configure (ctx, next) {
    ctx.template = templates.configure
    state.configure = render(ctx, {loading: true})
    getAndRenderAll(next)
  },
  detail: function detail (ctx, next) {
    ctx.template = templates.detail
    state.detail = render(ctx, {loading: true})
    getAndRender(ctx.params.name, next)
  },
  about: function about (ctx, next) {
    ctx.template = templates.about
    state.about = render(ctx, {})
  }
}

// set up routes
page('/', routes.configure)
page('/detail/:name', routes.detail)
page('/about', routes.about)

// initialize router
page.start()
page('/')

// Load all statuses when the app gets focused
client.on('show', function () {
  getAndRenderAll()
  var currentProcess = state.detail && state.detail.get('name')
  if (currentProcess) getAndRender(currentProcess)
})

function execTask (task) {
  client.request('task', task, function (err, data) {
    if (err) return throwError(err)
    if (!data) return

    if (Array.isArray(data)) {
      renderAll(data)
    } else if (data.name) {
      state.detail.set(data)
    }
  })
}

function render (ctx) {
  var ract = new Ractive({
    el: '#container',
    template: ctx.template,
    data: ctx.data
  })

  ract.on(events)
  return ract
}

function getAndRenderAll (callback) {
  callback = catchErrors(callback || function () {})
  client.request('get-all', function (err, data) {
    if (err) return callback(err)
    renderAll(data)
    callback()
  })
}

function renderAll (data) {
  data = data || []
  var obj = {items: data, hasProcesses: data.length > 0}
  state.configure.set(obj)
}

function getAndRender (name, callback) {
  callback = catchErrors(callback || function () {})
  client.request('get-one', {name: name}, function (err, data) {
    if (err) return callback(err)
    state.detail.set(data)
    callback()
  })
}

function catchErrors (callback) {
  return function throwErrorsOrContinue (err) {
    if (err) return throwError(err)
    callback()
  }
}

function throwError (error) {
  var message = error.stack || error.message || JSON.stringify(error)
  console.error(message)
  window.alert(message)
}

var path = require('path')
var fs = require('fs')

var menubar = require('menubar')
var ms = require('ms')
var Mongroup = require('mongroup')
var mkdir = require('mkdirp').sync
var debug = require('debug')('monu')
var shell = require('shell')
var dialog = require('dialog')

// try to fix the $PATH on OS X
require('fix-path')()

// use current user env (https://github.com/sindresorhus/shell-path/issues/7)
try {
  process.env = require('user-env')()
} catch (e) {}

var Server = require('electron-rpc/server')
var app = new Server()

var opts = {dir: __dirname, icon: path.join(__dirname, 'images', 'Icon.png')}
var menu = menubar(opts)
var conf

process.on('uncaughtException', function (err) {
  dialog.showErrorBox('Uncaught Exception: ' + err.message, err.stack || '')
  menu.app.quit()
})

menu.on('ready', function ready () {
  conf = loadConfig()
  var canQuit = false
  menu.app.on('will-quit', function tryQuit (e) {
    if (canQuit) return true
    menu.window = undefined
    e.preventDefault()
  })

  // start all once
  start([], function started (err) {
    if (err) return console.log('error starting processes: ' + err.message)
    console.log('started all processes')
  })

  menu.on('show', function show () {
    app.configure(menu.window.webContents)
    app.send('show')
  })

  app.on('terminate', function terminate (ev) {
    canQuit = true
    menu.app.terminate()
  })

  app.on('open-dir', function openDir (ev) {
    shell.showItemInFolder(path.join(conf.exec.cwd, 'config.json'))
  })

  app.on('open-logs-dir', function openLogsDir (req) {
    shell.showItemInFolder(path.join(conf.logs, req.body.name + '.log'))
  })

  app.on('get-all', function getAll (req, next) {
    next(null, getProcessesStatus())
  })

  app.on('get-one', function getOne (req, next) {
    next(null, getProcessStatus(req.body.name))
  })

  app.on('task', function task (req, next) {
    if (req.body.task === 'startAll') start([], updateAll)
    if (req.body.task === 'stopAll') stop([], req.body.signal, updateAll)
    if (req.body.task === 'restartAll') restart([], updateAll)
    if (req.body.task === 'start') start([req.body.name], updateSingle)
    if (req.body.task === 'stop') stop([req.body.name], req.body.signal, updateSingle)
    if (req.body.task === 'restart') restart([req.body.name], updateSingle)

    function updateAll (err) {
      if (err) throw err
      next(null, getProcessesStatus())
    }

    function updateSingle (err) {
      if (err) throw err
      next(null, getProcessStatus(req.body.name))
    }
  })
})

function loadConfig () {
  var dir = path.join(menu.app.getPath('userData'), 'data')
  var configFile = dir + '/config.json'
  var conf, data

  try {
    data = fs.readFileSync(configFile)
  } catch (e) {
    if (e.code === 'ENOENT') {
      mkdir(dir)
      fs.writeFileSync(configFile, fs.readFileSync(__dirname + '/config.json'))
      return loadConfig()
    } else {
      throw e
    }
  }

  try {
    conf = JSON.parse(data.toString())
  } catch (e) {
    var code = dialog.showMessageBox({
      message: 'Invalid configuration file\nCould not parse JSON',
      detail: e.stack,
      buttons: ['Reload Config', 'Exit app']
    })
    if (code === 0) {
      return loadConfig()
    } else {
      menu.app.quit()
      return
    }
  }

  conf.exec = {cwd: dir}
  conf.logs = path.resolve(path.join(dir, conf.logs || 'logs'))
  conf.pids = path.resolve(path.join(dir, conf.pids || 'pids'))

  mkdir(conf.logs)
  mkdir(conf.pids)

  conf.mon = path.join(__dirname, 'mon')
  return conf
}

function getProcessStatus (procName) {
  var procs = getProcessesStatus()
  return procs.filter(function filter (proc) {
    return proc.name === procName
  })[0]
}

function getProcessesStatus () {
  debug('reload config, get proc status...')
  conf = loadConfig()
  var group = new Mongroup(conf)
  var procs = group.procs

  return procs.map(function each (proc) {
    var uptime, state = proc.state()
    if (state === 'alive') uptime = ms(Date.now() - proc.mtime(), { long: true })
    var item = {
      cmd: proc.cmd,
      name: proc.name,
      state: state,
      pid: proc.pid,
      uptime: uptime ? uptime : undefined
    }

    return item
  })
}

function restart (procs, cb) {
  stop(procs, 'SIGQUIT', function onstop (err1) {
    start(procs, function onstart (err2) {
      if (cb) cb(err1 || err2)
    })
  })
}

function start (procs, cb) {
  var group = new Mongroup(conf)
  group.start(procs, function onstart (err) {
    if (err) return cb(err)
    cb()
  })
}

function stop (procs, signal, cb) {
  if (!signal) signal = 'SIGQUIT'
  var group = new Mongroup(conf)
  group.stop(procs, signal, function onstop (err) {
    if (!err || err.code === 'ENOENT') return cb()
    cb(err)
  })
}

