{
	"name": "datatables/datatables",
	"version": "1.10.21",
	"description": "DataTables is a plug-in for the jQuery Javascript library. It is a highly flexible tool, based upon the foundations of progressive enhancement, which will add advanced interaction controls to any HTML table.", 
	"homepage": "http://www.datatables.net/",
	"author": "SpryMedia", 
	"license": [
		"MIT"
	],
	"prefer-stable": true,
	"support": {
		"forum": "https://datatables.net/forums",
		"source": "https://github.com/DataTables/DataTablesSrc/"
	}
}

{
	"name": "DataTables",
	"version": "1.10.21",
	"description": "DataTables enhances HTML tables with the ability to sort, filter and page the data in the table very easily. It provides a comprehensive API and set of configuration options, allowing you to consume data from virtually any data source.",
	"homepage": "http://datatables.net/",
	"docs": "http://datatables.net/",
	"demo": "http://datatables.net/examples",
	"download": "http://datatables.net/download",
	"author": {
		"name": "Allan Jardine",
		"url": "http://sprymedia.co.uk"
	},
	"licenses": [
		{
			"type": "MIT",
			"url": "http://datatables.net/license_bsd"
		}
	],
	"dependencies": {
		"jquery": ">=1.7"
	},
	"keywords": [
		"DataTables",
		"DataTable",
		"table",
		"grid",
		"filter",
		"sort",
		"page",
		"internationalisable"
	]
}

{
	"name": "datatables",
	"version": "1.10.21",
	"title": "DataTables",
	"main": "media/js/jquery.dataTables",
	"files": [
		"media/js/jquery.dataTables.js",
		"media/js/jquery.dataTables.min.js",
		"media/css/jquery.dataTables.css",
		"media/css/jquery.dataTables.min.css",
		"media/images"
	],
	"license": "MIT",
	"dependencies": {
		"jquery": ">=1.7"
	},
	"author": {
		"name": "Allan Jardine",
		"url": "http://sprymedia.co.uk"
	},
	"description": "DataTables enhances HTML tables with the ability to sort, filter and page the data in the table very easily. It provides a comprehensive API and set of configuration options, allowing you to consume data from virtually any data source.",
	"keywords": [
		"DataTables",
		"DataTable",
		"table",
		"grid",
		"filter",
		"sort",
		"page",
		"internationalisable",
		"jquery-plugin"
	],
	"repository": {
		"type": "git",
		"url": "https://github.com/DataTables/DataTables.git"
	},
	"homepage": "http://datatables.net",

	"jspm": {
		"dependencies": {
			"css": "^0.1.5",
			"jquery": "*"
		},
		"registry": "jspm",
		"shim": {
			"media/js/jquery.dataTables": {
				"deps": ["jquery", "../css/jquery.dataTables.css!"],
				"exports": "$"
			}
		}
	}
}
