{
  "private": true,
  "name": "dummy.package",
  "description": "dummy package.json to allow standard tests to include .automation folder",
  "version": "1.0.0",
  "standard": {
    "ignore": [
      "!.automation/**"
    ]
  },
  "dependencies": {
    "@cspell/dict-medicalterms": "^4.0.0",
    "markdown-table-formatter": "^1.4.0"
  }
}

{
  $schema: 'https://docs.renovatebot.com/renovate-schema.json',
  extends: [
    'config:recommended',
    'customManagers:dockerfileVersions',
    ':maintainLockFilesWeekly',
    ':separateMultipleMajorReleases',
    ':semanticCommits',
    ':label(dependencies)',
    ':configMigration',
    ':combinePatchMinorReleases',
    'npm:unpublishSafe',
    'preview:dockerVersions',
    'schedule:daily',
    'group:unitTestNonMajor',
    ':enableVulnerabilityAlerts',
  ],
  vulnerabilityAlerts: {
    enabled: true,
  },
  osvVulnerabilityAlerts: true,
  packageRules: [
    {
      matchPackageNames: [
        'uv',
        'astral-sh/uv',
        'ghcr.io/astral-sh/uv',
      ],
      schedule: [
        'before 4am on Wednesday',
      ],
    },
    {
      groupName: 'Roslynator',
      matchSourceUrls: [
        'https://github.com/dotnet/roslynator',
      ],
      matchUpdateTypes: [
        'digest',
        'patch',
        'minor',
        'major',
      ],
    },
    {
      groupName: 'Salesforce packages',
      matchPackageNames: [
        '@salesforce/**',
      ],
      schedule: [
        'before 4am every 4 day',
      ],
      matchUpdateTypes: [
        'digest',
        'patch',
        'minor',
        'major',
      ],
    },
  ],
  customManagers: [
    {
      customType: 'regex',
      description: 'Update dockerfile sections in .megalinter-descriptor.yml files',
      fileMatch: [
        '(^|/).*\\.megalinter-descriptor.ya?ml$',
      ],
      matchStrings: [
        '# renovate: datasource=(?<datasource>[a-z-.]+?) depName=(?<depName>[^\\s]+?)(?: (lookupName|packageName)=(?<packageName>[^\\s]+?))?(?: versioning=(?<versioning>[^\\s]+?))?(?: extractVersion=(?<extractVersion>[^\\s]+?))?(?: registryUrl=(?<registryUrl>[^\\s]+?))?\\s(?:ENV|ARG)\\s+[A-Za-z0-9_]+?_VERSION[ =]["\']?(?<currentValue>.+?)["\']?\\s',
        '# renovate: datasource=(?<datasource>[a-z-.]+?) depName=(?<depName>[^\\s]+?)(?: (lookupName|packageName)=(?<packageName>[^\\s]+?))?(?: versioning=(?<versioning>[^\\s]+?))?(?: extractVersion=(?<extractVersion>[^\\s]+?))?(?: registryUrl=(?<registryUrl>[^\\s]+?))?\\s+(?:ENV|ARG)\\s+[A-Za-z0-9_]+?_VERSION[ =]["\']?(?<currentValue>.+?)["\']?\\s',
      ],
    },
  ],
}
