func TestInterface(t *testing.T) {

	var testCases = []struct {
		OriginalFoo Foo
		Path        string
		SetVal      interface{}
		ExpectedFoo Foo
	}{
		// set string field
		{
			Foo{FirstName: ""},
			"FirstName",
			"hi",
			Foo{FirstName: "hi"},
		},
		// set interface{} field
		{
			Foo{FirstName: "", GeneralInterface: nil},
			"GeneralInterface",
			"str",
			Foo{FirstName: "", GeneralInterface: "str"},
		},
		// set field of the interface{} field
		// Note: set uninitialized interface{} should fail
		// Note: interface of struct (not ptr to struct) should fail
		{
			Foo{FirstName: "", GeneralInterface: &Foo{FirstName: ""}}, // if Foo is not ptr this will fail
			"GeneralInterface.FirstName",
			"foo",
			Foo{FirstName: "", GeneralInterface: &Foo{FirstName: "foo"}},
		},
		// interface two level
		{
			Foo{FirstName: "", GeneralInterface: &Foo{GeneralInterface: nil}},
			"GeneralInterface.GeneralInterface",
			"val",
			Foo{FirstName: "", GeneralInterface: &Foo{GeneralInterface: "val"}},
		},
	}

	for idx, tc := range testCases {
		t.Run(fmt.Sprintf("test case #%d", idx+1), func(t *testing.T) {
			is := assert.New(t)

			err := Set(&tc.OriginalFoo, tc.SetVal, tc.Path)
			is.NoError(err)
			is.Equal(tc.ExpectedFoo, tc.OriginalFoo)
		})
	}

}

func TestSet_ErrorCaces(t *testing.T) {

	var testCases = []struct {
		OriginalFoo Foo
		Path        string
		SetVal      interface{}
	}{
		// uninit interface
		// Itf is not initialized so Set cannot properly allocate type
		{
			Foo{BarInterface: nil},
			"BarInterface.Name",
			"val",
		},
		{
			Foo{GeneralInterface: &Foo{BarInterface: nil}},
			"GeneralInterface.BarInterface.Name",
			"val",
		},
		// type mismatch
		{
			Foo{FirstName: ""},
			"FirstName",
			20,
		},
	}

	for idx, tc := range testCases {
		t.Run(fmt.Sprintf("test case #%d", idx+1), func(t *testing.T) {
			is := assert.New(t)

			err := Set(&tc.OriginalFoo, tc.SetVal, tc.Path)
			is.Error(err)
		})
	}

	t.Run("not pointer", func(t *testing.T) {
		is := assert.New(t)
		baz := Bar{Name: "dummy"}
		err := Set(baz, Bar{Name: "dummy2"}, "Name")
		is.Error(err)
	})

	t.Run("Unexported field", func(t *testing.T) {
		is := assert.New(t)
		s := struct {
			name string
		}{name: "dummy"}
		err := Set(&s, s, "name")
		is.Error(err)
	})
}

func TestMustSet_Basic(t *testing.T) {
	t.Run("Variable", func(t *testing.T) {
		is := assert.New(t)
		s := 1
		MustSet(&s, 2, "")
		is.Equal(2, s)
	})

	t.Run("Struct", func(t *testing.T) {
		is := assert.New(t)
		s := Bar{Name: "a"}
		MustSet(&s, "b", "Name")
		is.Equal("b", s.Name)
	})
}

// Examples

func ExampleSet() {

	var bar Bar = Bar{
		Name: "level-0",
		Bar: &Bar{
			Name: "level-1",
			Bars: []*Bar{
				{Name: "level2-1"},
				{Name: "level2-2"},
			},
		},
	}

	_ = Set(&bar, "level-0-new", "Name")
	fmt.Println(bar.Name)

	// discard error use MustSet
	MustSet(&bar, "level-1-new", "Bar.Name")
	fmt.Println(bar.Bar.Name)

	_ = Set(&bar, "level-2-new", "Bar.Bars.Name")
	fmt.Println(bar.Bar.Bars[0].Name)
	fmt.Println(bar.Bar.Bars[1].Name)

	// Output:
	// level-0-new
	// level-1-new
	// level-2-new
	// level-2-new
}

package funk

import (
	"math/rand"
	"testing"
)

const (
	seed      = 918234565
	sliceSize = 3614562
)

func sliceGenerator(size uint, r *rand.Rand) (out []int64) {
	for i := uint(0); i < size; i++ {
		out = append(out, rand.Int63())
	}
	return
}

func BenchmarkSubtract(b *testing.B) {
	r := rand.New(rand.NewSource(seed))
	testData := sliceGenerator(sliceSize, r)
	what := sliceGenerator(sliceSize, r)

	b.Run("Subtract", func(b *testing.B) {
		for n := 0; n < b.N; n++ {
			Subtract(testData, what)
		}
	})
}

func BenchmarkContains(b *testing.B) {
	r := rand.New(rand.NewSource(seed))
	testData := sliceGenerator(sliceSize, r)
	what := r.Int63()

	b.Run("ContainsInt64", func(b *testing.B) {
		for n := 0; n < b.N; n++ {
			ContainsInt64(testData, what)
		}
	})

	b.Run("IndexOfInt64", func(b *testing.B) {
		for n := 0; n < b.N; n++ {
			IndexOfInt64(testData, what)
		}
	})

	b.Run("Contains", func(b *testing.B) {
		for n := 0; n < b.N; n++ {
			Contains(testData, what)
		}
	})
}

func BenchmarkUniq(b *testing.B) {
	r := rand.New(rand.NewSource(seed))
	testData := sliceGenerator(sliceSize, r)

	b.Run("UniqInt64", func(b *testing.B) {
		for n := 0; n < b.N; n++ {
			UniqInt64(testData)
		}
	})

	b.Run("Uniq", func(b *testing.B) {
		for n := 0; n < b.N; n++ {
			Uniq(testData)
		}
	})
}

func BenchmarkSum(b *testing.B) {
	r := rand.New(rand.NewSource(seed))
	testData := sliceGenerator(sliceSize, r)

	b.Run("SumInt64", func(b *testing.B) {
		for n := 0; n < b.N; n++ {
			SumInt64(testData)
		}
	})

	b.Run("Sum", func(b *testing.B) {
		for n := 0; n < b.N; n++ {
			Sum(testData)
		}
