package funk

import (
	"errors"
	"fmt"
	"reflect"
)

// Fill fills elements of array with value
func Fill(in interface{}, fillValue interface{}) (interface{}, error) {
	inValue := reflect.ValueOf(in)
	inKind := inValue.Type().Kind()
	if inKind != reflect.Slice && inKind != reflect.Array {
		return nil, errors.New("can only fill slices and arrays")
	}

	inType := reflect.TypeOf(in).Elem()
	value := reflect.ValueOf(fillValue)
	if inType != value.Type() {
		return nil, fmt.Errorf(
			"cannot fill '%s' with '%s'", reflect.TypeOf(in), value.Type(),
		)
	}

	length := inValue.Len()
	newSlice := reflect.SliceOf(reflect.TypeOf(fillValue))
	in = reflect.MakeSlice(newSlice, length, length).Interface()
	inValue = reflect.ValueOf(in)

	for i := 0; i < length; i++ {
		inValue.Index(i).Set(value)
	}
	return in, nil
}

package funk

import (
	"testing"

	"github.com/stretchr/testify/assert"
)

func TestFillMismatchedTypes(t *testing.T) {
	_, err := Fill([]string{"a", "b"}, 1)
	assert.EqualError(t, err, "cannot fill '[]string' with 'int'")
}

func TestFillUnfillableTypes(t *testing.T) {
	var stringVariable string
	var uint32Variable uint32
	var boolVariable bool

	types := [](interface{}){
		stringVariable,
		uint32Variable,
		boolVariable,
	}

	for _, unfillable := range types {
		_, err := Fill(unfillable, 1)
		assert.EqualError(t, err, "can only fill slices and arrays")
	}
}

func TestFillSlice(t *testing.T) {
	input := []int{1, 2, 3}
	result, err := Fill(input, 1)
	assert.NoError(t, err)
	assert.Equal(t, []int{1, 1, 1}, result)

	// Assert that input does not change
	assert.Equal(t, []int{1, 2, 3}, input)
}

func TestFillArray(t *testing.T) {
	input := [...]int{1, 2, 3}
	result, err := Fill(input, 2)
	assert.NoError(t, err)
	assert.Equal(t, []int{2, 2, 2}, result)

	// Assert that input does not change
	assert.Equal(t, [...]int{1, 2, 3}, input)
}

