// ShuffleBool creates an array of bool shuffled values using FisherYates algorithm
func ShuffleBool(a []bool) []bool {
	for i := range a {
		j := rand.Intn(i + 1)
		a[i], a[j] = a[j], a[i]
	}

	return a
}

