func domainName(f *Faker) string {
	name := strings.Replace(strings.ToLower(jobDescriptor(f)+bs(f)), " ", "", -1)

	return fmt.Sprintf("%s.%s", name, domainSuffix(f))
}

// DomainSuffix will generate a random domain suffix
