func TestUUID(t *testing.T) {
	id := UUID()

	if len(id) != 36 {
		t.Error(id)
		t.Error("unique length does not equal requested length")
	}

	// Checking for race conditions, need to run --race
	for i := 0; i < 10000; i++ {
		go func() {
			_ = UUID()
		}()
	}
}

