		valueStr, ok := value.(string)
		if !ok {
			return errors.New("call to Fake method did not return a string")
		}
		v.SetString(valueStr)
	} else {
		genStr, err := generate(f, strings.Repeat("?", number(f, 4, 10)))
		if err != nil {
			return err
		}

		v.SetString(genStr)
	}

	return nil
}

