// Package main of OSSF Scorecard.
package main

import (
	"log"

	"github.com/ossf/scorecard/v5/cmd"
	"github.com/ossf/scorecard/v5/options"
)

func main() {
	opts := options.New()
	if err := cmd.New(opts).Execute(); err != nil {
		log.Fatalf("error during command execution: %v", err)
	}
}
