func loadDynamicTerminfo(term string) (*terminfo.Terminfo, error) {
	if term == "" {
		return nil, fmt.Errorf("%w: term not set", ErrTermNotFound)
	}
	ti, _, e := dynamic.LoadTerminfo(term)
	if e != nil {
		return nil, e
	}
	return ti, nil
}

