func TestSetSuccessResult(t *testing.T) {
	c := tc()
	var user *UserInfo
	url := "/search?username=imroc&type=json"

	resp, err := c.R().SetSuccessResult(&user).Get(url)
	assertSuccess(t, resp, err)
	tests.AssertEqual(t, "imroc", user.Username)

	user = &UserInfo{}
	resp, err = c.R().SetSuccessResult(user).Get(url)
	assertSuccess(t, resp, err)
