type MergeQuery struct {
	baseQuery
	returningQuery

	using   schema.QueryWithArgs
	on      schema.QueryWithArgs
	when    []schema.QueryAppender
	comment string
}

var _ Query = (*MergeQuery)(nil)

func NewMergeQuery(db *DB) *MergeQuery {
