		TokenParsingTest{
			Name:      "Single parameter function",
			Input:     "foo('bar')",
			Functions: map[string]ExpressionFunction{"foo": noop},
			Expected: []ExpressionToken{
				ExpressionToken{
					Kind:  FUNCTION,
					Value: noop,
				},
				ExpressionToken{
					Kind: CLAUSE,
				},
				ExpressionToken{
					Kind:  STRING,
					Value: "bar",
				},
				ExpressionToken{
					Kind: CLAUSE_CLOSE,
				},
			},
		},
		TokenParsingTest{
			Name:      "Multiple parameter function",
			Input:     "foo('bar', 1.0)",
			Functions: map[string]ExpressionFunction{"foo": noop},
			Expected: []ExpressionToken{
				ExpressionToken{
					Kind:  FUNCTION,
					Value: noop,
				},
				ExpressionToken{
					Kind: CLAUSE,
				},
				ExpressionToken{
					Kind:  STRING,
					Value: "bar",
				},
				ExpressionToken{
					Kind: SEPARATOR,
				},
				ExpressionToken{
					Kind:  NUMERIC,
					Value: 1.0,
				},
				ExpressionToken{
					Kind: CLAUSE_CLOSE,
				},
			},
		},
		TokenParsingTest{
			Name:      "Nested function",
			Input:     "foo(foo('bar'), 1.0, foo(2.0))",
			Functions: map[string]ExpressionFunction{"foo": noop},
			Expected: []ExpressionToken{
				ExpressionToken{
					Kind:  FUNCTION,
					Value: noop,
				},
				ExpressionToken{
					Kind: CLAUSE,
				},

				ExpressionToken{
					Kind:  FUNCTION,
					Value: noop,
				},
				ExpressionToken{
					Kind: CLAUSE,
				},
				ExpressionToken{
					Kind:  STRING,
					Value: "bar",
				},
				ExpressionToken{
					Kind: CLAUSE_CLOSE,
				},
				ExpressionToken{
					Kind: SEPARATOR,
				},

				ExpressionToken{
					Kind:  NUMERIC,
					Value: 1.0,
				},

				ExpressionToken{
					Kind: SEPARATOR,
				},

				ExpressionToken{
					Kind:  FUNCTION,
					Value: noop,
				},
				ExpressionToken{
					Kind: CLAUSE,
				},
				ExpressionToken{
					Kind:  NUMERIC,
					Value: 2.0,
				},
				ExpressionToken{
					Kind: CLAUSE_CLOSE,
				},

				ExpressionToken{
					Kind: CLAUSE_CLOSE,
				},
			},
		},
		TokenParsingTest{
			Name:      "Function with modifier afterwards (#28)",
			Input:     "foo() + 1",
			Functions: map[string]ExpressionFunction{"foo": noop},
			Expected: []ExpressionToken{
				ExpressionToken{
					Kind:  FUNCTION,
					Value: noop,
				},
				ExpressionToken{
					Kind: CLAUSE,
				},
				ExpressionToken{
					Kind: CLAUSE_CLOSE,
				},
				ExpressionToken{
					Kind:  MODIFIER,
					Value: "+",
				},
				ExpressionToken{
					Kind:  NUMERIC,
					Value: 1.0,
				},
			},
		},
		TokenParsingTest{
			Name:      "Function with modifier afterwards and comparator",
			Input:     "(foo()-1) > 3",
			Functions: map[string]ExpressionFunction{"foo": noop},
			Expected: []ExpressionToken{
				ExpressionToken{
					Kind: CLAUSE,
				},
