// TestReaper_ReuseRunning tests whether reusing the reaper if using
// testcontainers from concurrently multiple packages works as expected. In this
// case, global locks are without any effect as Go tests different packages
// isolated. Therefore, this test does not use the same logic with locks on
// purpose. We expect reaper creation to still succeed in case a reaper is
// already running for the same session id by returning its container instance
// instead.
func TestReaper_ReuseRunning(t *testing.T) {
	reaperDisable(t, false)

	const concurrency = 64

	timeout, cancel := context.WithTimeout(context.Background(), time.Minute)
	defer cancel()

	sessionID := SessionID()

	dockerProvider, err := NewDockerProvider()
	require.NoError(t, err, "new docker provider should not fail")

	obtainedReaperContainerIDs := make([]string, concurrency)
	var wg sync.WaitGroup
	for i := 0; i < concurrency; i++ {
		wg.Add(1)
		go func(i int) {
			defer wg.Done()
			spawner := &reaperSpawner{}
			reaper, err := spawner.reaper(timeout, sessionID, dockerProvider)
			cleanupReaper(t, reaper, spawner)
			require.NoError(t, err)

			obtainedReaperContainerIDs[i] = reaper.container.GetContainerID()
		}(i)
	}
	wg.Wait()

	// Assure that all calls returned the same container.
	firstContainerID := obtainedReaperContainerIDs[0]
	for i, containerID := range obtainedReaperContainerIDs {
		require.Equal(t, firstContainerID, containerID, "call %d should have returned same container id", i)
	}
}

func TestSpawnerBackoff(t *testing.T) {
	b := spawner.backoff()
	for i := 0; i < 100; i++ {
		require.LessOrEqual(t, b.NextBackOff(), time.Millisecond*250, "backoff should not exceed max interval")
	}
}

// cleanupReaper schedules reaper for cleanup if it's not nil.
func cleanupReaper(t *testing.T, reaper *Reaper, spawner *reaperSpawner) {
	t.Helper()

	if reaper == nil {
		return
	}

	t.Cleanup(func() {
		reaper.close()
		require.NoError(t, spawner.cleanup())
	})
}

// cleanupTermSignal ensures that termSignal
func cleanupTermSignal(t *testing.T, termSignal chan bool) {
	t.Helper()

	t.Cleanup(func() {
		if termSignal != nil {
			termSignal <- true
		}
	})
}

package testcontainers

import (
	"context"

	"github.com/testcontainers/testcontainers-go/internal/core"
)

// Deprecated: use MustExtractDockerHost instead.
func ExtractDockerSocket() string {
	return MustExtractDockerSocket(context.Background())
}

// MustExtractDockerSocket Extracts the docker socket from the different alternatives, removing the socket schema.
// Use this function to get the docker socket path, not the host (e.g. mounting the socket in a container).
// This function does not consider Windows containers at the moment.
// The possible alternatives are:
//
//  1. Docker host from the "tc.host" property in the ~/.testcontainers.properties file.
//  2. The TESTCONTAINERS_DOCKER_SOCKET_OVERRIDE environment variable.
//  3. Using a Docker client, check if the Info().OperativeSystem is "Docker Desktop" and return the default docker socket path for rootless docker.
//  4. Else, Get the current Docker Host from the existing strategies: see MustExtractDockerHost.
//  5. If the socket contains the unix schema, the schema is removed (e.g. unix:///var/run/docker.sock -> /var/run/docker.sock)
//  6. Else, the default location of the docker socket is used (/var/run/docker.sock)
//
// It panics if a Docker client cannot be created, or the Docker host cannot be discovered.
func MustExtractDockerSocket(ctx context.Context) string {
	return core.MustExtractDockerSocket(ctx)
}

// SessionID returns a unique session ID for the current test session. Because each Go package
// will be run in a separate process, we need a way to identify the current test session.
// By test session, we mean:
//   - a single "go test" invocation (including flags)
//   - a single "go test ./..." invocation (including flags)
//   - the execution of a single test or a set of tests using the IDE
//
// As a consequence, with the sole goal of aggregating test execution across multiple
// packages, this variable will contain the value of the parent process ID (pid) of the current process
// and its creation date, to use it to generate a unique session ID. We are using the parent pid because
// the current process will be a child process of:
//   - the process that is running the tests, e.g.: "go test";
//   - the process that is running the application in development mode, e.g. "go run main.go -tags dev";
//   - the process that is running the tests in the IDE, e.g.: "go test ./...".
//
// Finally, we will hash the combination of the "testcontainers-go:" string with the parent pid
// and the creation date of that parent process to generate a unique session ID.
//
// This sessionID will be used to:
//   - identify the test session, aggregating the test execution of multiple packages in the same test session.
//   - tag the containers created by testcontainers-go, adding a label to the container with the session ID.
func SessionID() string {
	return core.SessionID()
}

package testcontainers

import (
	"os"
	"os/exec"
	"regexp"
	"testing"

	"github.com/stretchr/testify/require"
)

func TestSessionID(t *testing.T) {
	t.Run("SessionID() returns a non-empty string", func(t *testing.T) {
		sessionID := SessionID()
		require.NotEmptyf(t, sessionID, "SessionID() returned an empty string")
	})

	t.Run("Multiple calls to SessionID() return the same value", func(t *testing.T) {
		sessionID1 := SessionID()
		sessionID2 := SessionID()
		require.Equalf(t, sessionID1, sessionID2, "SessionID() returned different values: %s != %s", sessionID1, sessionID2)
	})

	t.Run("Multiple calls to SessionID() in multiple goroutines return the same value", func(t *testing.T) {
		sessionID1 := ""
		sessionID2 := ""

		done := make(chan bool)
		go func() {
			sessionID1 = SessionID()
			done <- true
		}()

		go func() {
			sessionID2 = SessionID()
			done <- true
		}()

		<-done
		<-done

		require.Equalf(t, sessionID1, sessionID2, "SessionID() returned different values: %s != %s", sessionID1, sessionID2)
	})

	t.Run("SessionID() from different child processes returns the same value", func(t *testing.T) {
		args := []string{"test", "./...", "-v", "-run", "TestSessionIDHelper"}
		env := append(os.Environ(), "TESTCONTAINERS_SESSION_ID_HELPER=1")

		re := regexp.MustCompile(">>>(.*)<<<")

		cmd1 := exec.Command("go", args...)
		cmd1.Env = env
		stdoutStderr1, err := cmd1.CombinedOutput()
		require.NoErrorf(t, err, "cmd1.Run() failed with %s", err)
		sessionID1 := re.FindString(string(stdoutStderr1))

		cmd2 := exec.Command("go", args...)
		cmd2.Env = env
		stdoutStderr2, err := cmd2.CombinedOutput()
		require.NoErrorf(t, err, "cmd2.Run() failed with %s", err)
		sessionID2 := re.FindString(string(stdoutStderr2))

		require.Equalf(t, sessionID1, sessionID2, "SessionID() returned different values: %s != %s", sessionID1, sessionID2)
	})
}

