// Package terramate provides functions for managing terraform stacks.
// A stack is a unit of independent runnable terraform modules.
package terramate

// Copyright 2023 Terramate GmbH
// SPDX-License-Identifier: MPL-2.0

// This file is required by the "preview" script.

generate_hcl "_test_mock.tf" {
  condition = tm_anytrue([
    tm_contains(terramate.stack.tags, "golang"),
    tm_contains(terramate.stack.tags, "e2etests"),
  ])
  lets {
    name = tm_ternary(terramate.stack.path.basename == "/", "terramate", terramate.stack.path.basename)
  }
  content {
    tm_dynamic "resource" {
      labels = ["local_file", let.name]
      attributes = {
        content  = <<-EOF
          ${terramate.stack.description}
        EOF
        filename = "${path.module}/mock-${let.name}.ignore"
      }
    }
  }
}

// Copyright 2023 Terramate GmbH
// SPDX-License-Identifier: MPL-2.0

generate_file "/.golangci.toml" {
  context = root

  lets {
    config = {
      linters = {
        enable = [
          "revive",
          "misspell",
          "gofmt",
          "bodyclose",
        ]
      }
      issues = {
        "exclude-rules" = [
          {
            path    = "(.+)_test\\.go"
            text    = "dot-imports:"
            linters = ["revive"]
          }
        ]
        exclude-use-default = false
      }

      run = {
        timeout : "15m"
      }
    }
  }

  content = tm_tomlencode(let.config)
}

// Copyright 2023 Terramate GmbH
// SPDX-License-Identifier: MPL-2.0

package terramate_test

import "github.com/rs/zerolog"

func init() {
	zerolog.SetGlobalLevel(zerolog.Disabled)
}

// Copyright 2023 Terramate GmbH
// SPDX-License-Identifier: MPL-2.0

globals {
  planfile = "out.tfplan"
}

script "test" {
  name = "Terramate tests"

  job {
    commands = [
      ["bash", "-c", "terraform init -lock=false >/dev/null"],
      ["bash", "-c", "terraform plan -out=${global.planfile} >/dev/null"]
    ]
  }

  job {
    command = [
    "go", "test", "-race", "-count=1", "-timeout", "30m"]
  }
}

script "preview" {
  name = "Preview Terramate tests"

  job {
    commands = [
      ["bash", "-c", "terraform init -lock=false >/dev/null"],
      ["bash", "-c", "terraform plan -out=${global.planfile} >/dev/null"]
    ]
  }

  job {
    command = [
      "go", "test", "-race", "-count=1", "-timeout", "30m", {
        sync_preview        = true,
        terraform_plan_file = global.planfile,
      }
    ]
  }
}

script "deploy" {
  name = "Deploy Terramate tests"

  job {
    commands = [
      ["bash", "-c", "terraform init -lock=false >/dev/null"],
      ["bash", "-c", "terraform plan -out=${global.planfile} >/dev/null"]
    ]
  }

  job {
    command = [
      "go", "test", "-race", "-count=1", "-timeout", "30m", {
        sync_deployment     = true,
        terraform_plan_file = global.planfile,
      }
    ]
  }
}

// Copyright 2024 Terramate GmbH
