var path = require("path");
module.exports = {
  extends: [
    "airbnb",
    "plugin:jest/recommended",
    "plugin:react-hooks/recommended",
    "plugin:@typescript-eslint/recommended",
    "plugin:prettier/recommended",
    "plugin:storybook/recommended",
  ],
  parser: "@typescript-eslint/parser",
  plugins: ["jest", "react", "@typescript-eslint"],
  env: {
    node: true,
    mocha: true,
    browser: true,
    "jest/globals": true,
  },
  globals: {
    expect: false,
    describe: false,
  },
  rules: {
    camelcase: "off",
    "consistent-return": 1,
    "arrow-body-style": 0,
    "max-len": 0,
    "no-unused-expressions": 0,
    "no-console": 0,
    "space-before-function-paren": 0,
    "react/prefer-stateless-function": 0,
    "react/no-multi-comp": 0,
    "react/no-unused-prop-types": [
      1,
      {
        customValidators: [],
        skipShapeProps: true,
      },
    ],
    "react/require-default-props": 0,
    // TODO set default props and enable this check
    "react/jsx-filename-extension": [
      1,
      {
        extensions: [".jsx", ".tsx"],
      },
    ],
    "react-hooks/exhaustive-deps": 1,
    "no-param-reassign": 0,
    "new-cap": 0,
    "import/no-unresolved": [
      2,
      {
        caseSensitive: false,
      },
    ],
    "linebreak-style": 0,
    "import/no-named-as-default": "off",
    "import/no-named-as-default-member": "off",
    "import/extensions": 0,
    "import/no-extraneous-dependencies": 0,
    "no-underscore-dangle": 0,
    "jsx-a11y/no-static-element-interactions": "off",
    // note you must disable the base rule as it can report incorrect errors. more info here:
    // https://github.com/typescript-eslint/typescript-eslint/blob/master/packages/eslint-plugin/docs/rules/no-use-before-define.md
    "no-use-before-define": "off",
    "@typescript-eslint/no-use-before-define": ["error"],
    // turn off and override to not run this on js and jsx files. More info here:
    // https://github.com/typescript-eslint/typescript-eslint/blob/master/packages/eslint-plugin/docs/rules/explicit-module-boundary-types.md#configuring-in-a-mixed-jsts-codebase
    "@typescript-eslint/explicit-module-boundary-types": "off",
    // Most of the js modules written by us need to be rewritten into TS. Until then
    // we use ts-ignore comment to ignore the error TS gives us from not having those modules
    // declared (TS7016). This is done on purpose as there is not time to rewrite everything in TS.
    "@typescript-eslint/ban-ts-comment": "off",
    "no-shadow": "off",
    // replaced by ts-eslint rule below
    "@typescript-eslint/no-shadow": "error",
    // There is a bug with these rules in our version of jsx-a11y plugin (5.1.1)
    // To upgrade our version of the plugin we would need to make more changes
    // with eslint-config-airbnb, so we will just turn off for now.
    "jsx-a11y/heading-has-content": "off",
    "jsx-a11y/anchor-has-content": "off",
  },
  overrides: [
  ],
  settings: {
    "import/resolver": {
      webpack: {
        config: path.join(__dirname, "webpack.config.js"),
      },
    },
  },
};

{
"presets": [
  [
    "@babel/preset-env",
    {
      "targets": "defaults",
      "useBuiltIns": "usage",
      "corejs": "3.8"
    }
  ],
  "@babel/preset-react",
  "@babel/preset-typescript"
],
"env": {
  "dev": {
    "presets": [
      "react-hmre"
    ]
  },
  "test": {
    "presets": [
      "@babel/preset-typescript"
    ]
  }
},
"plugins": [
  "@babel/plugin-syntax-dynamic-import",
  "@babel/plugin-syntax-import-meta",
  "@babel/plugin-proposal-class-properties",
  "@babel/plugin-proposal-json-strings",
  [
    "@babel/plugin-proposal-decorators",
    {
      "legacy": true
    }
  ],
  "@babel/plugin-proposal-function-sent",
  "@babel/plugin-proposal-export-namespace-from",
  "@babel/plugin-proposal-numeric-separator",
  "@babel/plugin-proposal-throw-expressions",
  "@babel/plugin-proposal-export-default-from",
  "@babel/plugin-proposal-logical-assignment-operators",
  "@babel/plugin-proposal-optional-chaining",
  [
    "@babel/plugin-proposal-pipeline-operator",
    {
      "proposal": "minimal"
    }
  ],
  "@babel/plugin-proposal-nullish-coalescing-operator",
  "@babel/plugin-proposal-do-expressions",
  "@babel/plugin-proposal-function-bind"
]
}

module.exports = {
  plugins: [
    require('autoprefixer')
  ]
}

//go:build tools
// +build tools

package tools

import (
	_ "github.com/fleetdm/fleet/v4/server/goose"
	_ "github.com/kevinburke/go-bindata"
	_ "github.com/quasilyte/go-ruleguard/dsl"
)

require("es6-promise").polyfill();

const path = require("path");
const webpack = require("webpack");
const bourbon = require("node-bourbon").includePaths;
const HtmlWebpackPlugin = require("html-webpack-plugin");
const MiniCssExtractPlugin = require("mini-css-extract-plugin");
const WebpackNotifierPlugin = require("webpack-notifier");
const ForkTsCheckerWebpackPlugin = require("fork-ts-checker-webpack-plugin");
const globImporter = require("node-sass-glob-importer");

const DEV_SOURCE_MAPS = "eval-source-map";

let plugins = [
  new ForkTsCheckerWebpackPlugin(),
  new HtmlWebpackPlugin({
    filename: "../frontend/templates/react.tmpl",
    inject: false,
    templateParameters: {
      isProduction: process.env.NODE_ENV === "production",
    },
    template: "frontend/templates/react.ejs",
  }),
  new WebpackNotifierPlugin({
    excludeWarnings: true,
  }),
];

if (process.env.NODE_ENV === "production") {
  plugins = plugins.concat([
    new webpack.DefinePlugin({
      "process.env": { NODE_ENV: JSON.stringify("production") },
    }),
    new MiniCssExtractPlugin({
      filename: "bundle-[contenthash].css",
    }),
  ]);
} else {
  // development
  plugins = plugins.concat([
    new MiniCssExtractPlugin({ filename: "bundle.css" }),
  ]);
}

const repo = __dirname;

const config = {
  mode: process.env.NODE_ENV,
  entry: {
    bundle: path.join(repo, "frontend/index.jsx"),
  },
  output: {
    path: path.join(repo, "assets/"),
    publicPath: "/assets/",
    filename: "[name].js",
  },
  devtool: process.env.NODE_ENV === "development" ? DEV_SOURCE_MAPS : false,
  plugins,
  optimization: {
    minimize: process.env.NODE_ENV === "production",
  },
  module: {
    // The following noParse suppresses the warning about sqlite-parser being a
    // pre-compiled JS file. See https://goo.gl/N4s6bB.
    noParse: /node_modules\/sqlite-parser\/dist\/sqlite-parser-min.js/,
    rules: [
      {
        test: /\.(pdf|png|gif|ico|jpg|svg|eot|otf|woff|woff2|ttf|mp4|webm)$/,
        type: "asset",
        generator: {
          filename: "[name]@[hash][ext]",
        },
      },
      {
        test: /\.(sh|ps1)$/,
        type: "asset/source",
      },
      {
        test: /(\.tsx?|\.jsx?)$/,
        exclude: /node_modules/,
        use: {
          loader: "esbuild-loader",
          options: {
            loader: "tsx", // Or 'ts' if you don't need tsx
            target: "es2016",
          },
        },
      },
      {
        test: /\.scss$/,
        exclude: /node_modules/,
        use: [
          {
            loader: MiniCssExtractPlugin.loader,
            options: {
              publicPath: "./",
            },
          },
          { loader: "css-loader" },
          { loader: "postcss-loader" },
          {
            loader: "sass-loader",
            options: {
              sourceMap: true,
              sassOptions: {
                includePaths: bourbon,
                importer: globImporter(),
              },
            },
          },
        ],
      },
      {
        test: /\.css$/,
        use: [
          {
            loader: MiniCssExtractPlugin.loader,
            options: {},
          },
          "css-loader",
          "postcss-loader",
        ],
      },
      {
        test: /\.jsx?$/,
        include: path.join(repo, "frontend"),
        use: { loader: "babel-loader", options: { cacheDirectory: true } },
      },
    ],
  },
  resolve: {
    extensions: [".tsx", ".ts", ".js", ".jsx", ".json"],
    modules: [path.resolve(path.join(repo, "./frontend")), "node_modules"],
    fallback: { path: require.resolve("path-browserify") },
  },
};

if (process.env.NODE_ENV === "production") {
  config.output.filename = "[name]-[contenthash].js";
}

module.exports = config;
