package notify

import (
	"context"
	"errors"

	"golang.org/x/sync/errgroup"
)

// send calls the underlying notification services to send the given subject and message to their respective endpoints.
func (n *Notify) send(ctx context.Context, subject, message string) error {
	if n.Disabled {
		return nil
	}
	if ctx == nil {
		ctx = context.Background()
	}

	var eg errgroup.Group
	for _, service := range n.notifiers {
		if service == nil {
			continue
		}

		eg.Go(func() error {
			return service.Send(ctx, subject, message)
		})
	}

	err := eg.Wait()
	if err != nil {
		err = errors.Join(ErrSendNotification, err)
	}

	return err
}

// Send calls the underlying notification services to send the given subject and message to their respective endpoints.
func (n *Notify) Send(ctx context.Context, subject, message string) error {
	return n.send(ctx, subject, message)
}

// Send calls the underlying notification services to send the given subject and message to their respective endpoints.
func Send(ctx context.Context, subject, message string) error {
	return std.Send(ctx, subject, message)
}

package notify

import (
	"context"
	"testing"

	"github.com/nikoksr/notify/service/mail"
)

func TestNotifySend(t *testing.T) {
	t.Parallel()

	n := New()
	if n.Disabled {
		t.Fatal("New() returned disabled Notifier")
	}

	// Nil context
	//nolint:staticcheck // Purposefully testing with nil context.
	if err := n.Send(nil, "subject", "message"); err != nil {
		t.Errorf("Send() returned error: %v", err)
	}
	if r := recover(); r != nil {
		t.Errorf("Send() panicked: %v", r)
	}

	ctx := context.Background()
	if err := n.Send(ctx, "subject", "message"); err != nil {
		t.Errorf("Send() returned error: %v", err)
	}

	// This is not meant to test the mail service, but rather the general capability of the Send() function to catch
	// errors.
	n.UseServices(mail.New("", ""))
	if err := n.Send(ctx, "subject", "message"); err == nil {
		t.Errorf("Send() invalid mail returned no error: %v", err)
	}

	// After disabling the Notifier, Send() should return silently.
	n.WithOptions(Disable)

	if err := n.Send(ctx, "subject", "message"); err != nil {
		t.Errorf("Send() of disabled Notifier returned error: %v", err)
	}

	n.WithOptions(Enable)

	// Smuggle in a nil service. This usually never happens, since UseServices filters out nil services. But, it's good
	// to test anyway.
	n.notifiers = make([]Notifier, 0)
	n.notifiers = append(n.notifiers, nil)

	if err := n.Send(ctx, "subject", "message"); err != nil {
		t.Errorf("Send() of disabled Notifier returned no error: %v", err)
	}
	if r := recover(); r != nil {
		t.Errorf("Send() with nil service panicked: %v", r)
	}
}

func TestSendMany(t *testing.T) {
	t.Parallel()

	n := New()
	if n == nil {
		t.Fatal("New() returned nil")
	}

	var services []Notifier

	for range 10 {
		services = append(services, mail.New("", ""))
	}

	n.UseServices(services...)

	if err := n.Send(context.Background(), "subject", "message"); err == nil {
		t.Errorf("Send() invalid mail returned no error: %v", err)
	}
}

package notify

// useService adds a given service to the Notifier's services list.
func (n *Notify) useService(service Notifier) {
	if service != nil {
		n.notifiers = append(n.notifiers, service)
	}
}

// useServices adds the given service(s) to the Notifier's services list.
func (n *Notify) useServices(services ...Notifier) {
	for _, s := range services {
		n.useService(s)
	}
}

// UseServices adds the given service(s) to the Notifier's services list.
func (n *Notify) UseServices(services ...Notifier) {
	n.useServices(services...)
}

// UseServices adds the given service(s) to the Notifier's services list.
func UseServices(services ...Notifier) {
	std.UseServices(services...)
}

package notify

import (
	"testing"

	"github.com/nikoksr/notify/service/mail"
)

func TestUseServices(t *testing.T) {
	t.Parallel()

	n := New()
	if len(n.notifiers) != 0 {
		t.Fatalf("Expected len(n.notifiers) == 0, got %d", len(n.notifiers))
	}

	n.UseServices(mail.New("", ""))

	if len(n.notifiers) != 1 {
		t.Errorf("Expected len(n.notifiers) == 1, got %d", len(n.notifiers))
	}

	n.UseServices(
		mail.New("", ""),
		mail.New("", ""),
	)

	if len(n.notifiers) != 3 {
		t.Errorf("Expected len(n.notifiers) == 3, got %d", len(n.notifiers))
	}

	n.UseServices(nil)
	if r := recover(); r != nil {
		t.Errorf("Expected no panic, got %v", r)
	}
}

