package olric

import (
	"errors"
	"time"

	"github.com/buraksezer/olric/internal/resp"
	"github.com/buraksezer/olric/pkg/storage"
)

var ErrNilResponse = errors.New("storage entry is nil")

type GetResponse struct {
	entry storage.Entry
}

func (g *GetResponse) Scan(v interface{}) error {
	if g.entry == nil {
		return ErrNilResponse
	}
	return resp.Scan(g.entry.Value(), v)
}

func (g *GetResponse) Int() (int, error) {
	v := new(int)
	err := g.Scan(v)
	if err != nil {
		return 0, err
	}
	return *v, nil
}

func (g *GetResponse) String() (string, error) {
	v := new(string)
	err := g.Scan(v)
	if err != nil {
		return "", err
	}
	return *v, nil
}

func (g *GetResponse) Int8() (int8, error) {
	v := new(int8)
	err := g.Scan(v)
	if err != nil {
		return 0, err
	}
	return *v, nil
}

func (g *GetResponse) Int16() (int16, error) {
	v := new(int16)
	err := g.Scan(v)
	if err != nil {
		return 0, err
	}
	return *v, nil
}

func (g *GetResponse) Int32() (int32, error) {
	v := new(int32)
	err := g.Scan(v)
	if err != nil {
		return 0, err
	}
	return *v, nil
}

func (g *GetResponse) Int64() (int64, error) {
	v := new(int64)
	err := g.Scan(v)
	if err != nil {
		return 0, err
	}
	return *v, nil
}

func (g *GetResponse) Uint() (uint, error) {
	v := new(uint)
	err := g.Scan(v)
