{
  "presets": ["@babel/env", "@babel/react", "@babel/preset-typescript"],
  "plugins": ["@babel/plugin-proposal-class-properties", "macros"]
}

/* eslint-disable */

module.exports = {
  root: true,
  parser: "@typescript-eslint/parser",
  parserOptions: {
    ecmaFeatures: { jsx: true },
  },
  settings: {
    react: {
      version: "detect",
    },
  },
  extends: [
    "eslint:recommended",
    "plugin:@typescript-eslint/eslint-recommended",
    "plugin:@typescript-eslint/recommended",
    "plugin:react/recommended",
    "plugin:prettier/recommended",
  ],
  rules: {
    // Include .prettierrc.js rules
    "prettier/prettier": ["error", {}, { usePrettierrc: true }],
    "react/react-in-jsx-scope": "off",
    "@typescript-eslint/no-unused-vars": ["error", { varsIgnorePattern: "_" }],
    "react/prop-types": "off",
    "react/jsx-no-target-blank": ["error", { allowReferrer: true }],
    "@typescript-eslint/no-explicit-any": "off", // TODO: turn this on in future
    "@typescript-eslint/explicit-module-boundary-types": "off", // TODO: turn this on in future
  },
}

const global = (1, eval)("this")
global.global = global
global.globalThis = global
global.frames = global
global.self = global

const document = {
  documentElement: {},
  getElementById: () => undefined,
}

const window = {
  document,
  location: {
    href: "",
  },
}

const navigator = {}
global.navigator = navigator
global.window = window
global.document = document

// Intl polyfill is required until v8go supports Intl
class NoopFormat {
  format(arg0) {
    return arg0 ? arg0.toString() : ""
  }
}

global.Intl = {
  NumberFormat: NoopFormat,
  DateTimeFormat: NoopFormat,
}

/* eslint-disable @typescript-eslint/no-var-requires */
/* eslint-disable no-undef */

const fs = require("fs")
const esbuild = require("esbuild")
const babel = require("@babel/core")

// Replace with NPM package when this is resolved: https://github.com/nativew/esbuild-plugin-babel/issues/7
const babelPlugin = (options = {}) => ({
  name: "babel",
  setup(build, { transform } = {}) {
    const { filter = /.*/, namespace = "", config = {} } = options

    const transformContents = ({ args, contents }) => {
      const babelOptions = babel.loadOptions({
        ...config,
        filename: args.path,
        caller: {
          name: "esbuild-plugin-babel",
          supportsStaticESM: true,
        },
      })
      if (!babelOptions) return { contents }

      if (babelOptions.sourceMaps) {
        const filename = path.relative(process.cwd(), args.path)

        babelOptions.sourceFileName = filename
      }

      return new Promise((resolve, reject) => {
        babel.transform(contents, babelOptions, (error, result) => {
          error ? reject(error) : resolve({ contents: result.code })
        })
      })
    }

    if (transform) return transformContents(transform)

    build.onLoad({ filter, namespace }, async (args) => {
      const contents = await fs.promises.readFile(args.path, "utf8")

      return transformContents({ args, contents })
    })
  },
})

const emptyCSS = {
  name: "empty-css-imports",
  setup(build) {
    build.onLoad({ filter: /\.(css|scss)$/ }, () => ({ contents: "" }))
  },
}

const emptySVG = {
  name: "empty-svg-imports",
  setup(build) {
    build.onLoad({ filter: /\.(svg)$/ }, async (args) => {
      let contents = await fs.promises.readFile(args.path, "utf8")
      let buff = Buffer.from(contents).toString("base64")
      return { contents: `data:image/svg+xml;base64,${buff}`, loader: "text" }
    })
  },
}

esbuild
  .build({
    entryPoints: ["./public/ssr.tsx"],
    bundle: true,
    define: {
      "process.env.NODE_ENV": `"${process.env.NODE_ENV || "development"}"`,
    },
    inject: ["./esbuild-shim.js"],
    outfile: "ssr.js",
    plugins: [emptyCSS, emptySVG, babelPlugin()],
  })
  .catch(() => process.exit(1))

interface GetPriceResponse {
  price: { net: string }
  error?: {
    message: string
  }
}

interface PaddleSdk {
  isReady: boolean
  Setup(params: { vendor: number }): void
  Environment: {
    set(envName: "sandbox"): void
  }
  Checkout: {
    open(params: { override: string; closeCallback: () => void }): void
  }
  Product: {
    Prices(planId: number, callback: (resp: GetPriceResponse) => void): void
  }
}
declare interface Window {
  ga?: (cmd: string, evt: string, args?: any) => void
  set: (key: string, value: any) => void
  Paddle: PaddleSdk
}

interface SpriteSymbol {
  id: string
  viewBox: string
}

declare let __webpack_nonce__: string
declare let __webpack_public_path__: string

declare module "*.svg" {
  const content: SpriteSymbol
  export default content
}

package main

import (
	"os"

	"github.com/getfider/fider/app/cmd"
	_ "github.com/lib/pq"
)

func main() {
	args := os.Args[1:]
	if len(args) > 0 && args[0] == "ping" {
		os.Exit(cmd.RunPing())
	} else if len(args) > 0 && args[0] == "migrate" {
		os.Exit(cmd.RunMigrate())
	} else {
		os.Exit(cmd.RunServer())
	}
}

